//row 변경시 
function dsOutPrivitWork_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	if (dsOutPrivitWork.GetRowType(nRow) == "insert")
	{
		edACT_NM.Enable = true;
		calACT_DT.Enable = true;
	}
	else
	{
		edACT_NM.Enable = false;
		calACT_DT.Enable = false;
	}
	fcFileSearch(obj,nOldRow,nRow);
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutPrivitWork_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutPrivitWork.Row = LATEST_ROW_POS;
}

function grdViewFile_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sLocalFileName  = fce_GetSelectFile("Save", dsOutUploadFile.GetColumn(nRow, "FILE_NM"));
    var sServerFileName = dsOutUploadFile.GetColumn(nRow, "FILE_URL");
    var sFileSize       = dsOutUploadFile.GetColumn(nRow, "FILE_SIZE");
    if ( sLocalFileName == null ) return;

	fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsOutUploadFile);
}
