var sFormId       = 'E05070030';
var sBUTTONLIST   = 'FTFFTT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var bPopup = true;
var bChanged = false;
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FTTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	//검색조건 : 등록자
	cmbSearch.Index = 1;
	edtSearch.Text = Trim(gdsUser.USER_NM);

	btnDelRow.Enable = false;
	divBtnLst.btnPrint.enable = false;

	if ( IsExistVar("Arg_sRaceDay") && bPopup ) {
		calStrDt.Value = Arg_sRaceDay;
		calEndDt.Value = Arg_sRaceDay;

		bPopup = false;
	} else {       
		calStrDt.Value = fnc_calDate(fnc_currDate(), 90, true);
		calEndDt.Value = fnc_currDate();
	}

	// 화면 초기화
	fcInit();
	fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if (!fnc_FormUnloadCheck(sDataset, sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutBrchRtrn.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

    if( !fnc_IsMandatory(calStrDt, "시작일") ) return false;
    if( !fnc_IsMandatory(calEndDt, "종료일") ) return false;

    dsOutBrchRtrn.ClearData();
    dsOutRacerRank.ClearData();

    edtCustName.Text = "";
    edtCustTel.Text = "";
    edtCustAddr.Text = "";
    edtVehNo.Text = "";
    edtVehColor.Text = "";

    edtWinner.Text = "";

    edtExaRate.Text = "";
    edtExa1.Text = "";
    edtExa2.Text = "";
    edtExa3.Text = "";
    edtExa4.Text = "";
    edtExa5.Text = "";

    edtQuiRate.Text = "";
    edtQui1.Text = "";
    edtQui2.Text = "";
    edtQui3.Text = "";
    edtQui4.Text = "";
    edtQui5.Text = "";

    edtTriRate.Text = "";
    edtTri1.Text = "";
    edtTri2.Text = "";
    edtTri3.Text = "";
    edtTri4.Text = "";
    edtTri5.Text = "";

    edtTotalReturn.Text = "";
    edtBuyPlc.Text = "";
    edtReturnPlc.Text = "";
    edtRmk.Text = "";

    btnDelRow.Enable = false;
    divBtnLst.btnPrint.enable = false;

    imgPhoto.ImageID = "";
    dsProgBar.SetColumn(0,"FILE_NM","");
    dsProgBar.SetColumn(0,"FILE_PATH","");
    dsProgBar.SetColumn(0,"FILE_SIZE","");
    dsProgBar.SetColumn(0,"PROG_BAR","");

    return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "e05070030-service:searchBrchRtrn";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutBrchRtrn=dsOutBrchRtrn ";

    fce_AddParam("STR_DT", Trim(calStrDt.Value));
    fce_AddParam("END_DT", Trim(calEndDt.Value));
    fce_AddParam("SEARCH1", Trim(cmbSearch.Value));
    fce_AddParam("SEARCH2", Trim(edtSearch.Text));
    if(Trim(cmbBrch.Text) == "전체"){
        fce_AddParam("OBT_NM", "");
    }else{
        fce_AddParam("OBT_NM", Trim(cmbBrch.Text));
    }


    fce_AddParam("RATE1", Trim(cmbPoolCd.Value));
    fce_AddParam("RATE2", Trim(edtRate.Text));

    var str = "";
    if(edtRaturn.Text == "" || edtRaturn.Text == null){
        str = "0";
    } else {
        str = edtRaturn.Text;
    }
    fce_AddParam("RETURN_MONEY", Trim(str));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		dsOutPoolCd.InsertRow(0);
		dsOutPoolCd.SetColumn(0, "CD"   , "");
		dsOutPoolCd.SetColumn(0, "CD_NM", "선택");
		dsOutPoolCd.UnFilter();
        dsOutPoolCd.Filter("CD!='001'&&CD!='002'");

		dsOutBrchCd.InsertRow(0);
		dsOutBrchCd.SetColumn(0, "CD", "");
		dsOutBrchCd.SetColumn(0, "CD_NM", "전체");

		cmbPoolCd.Index = 0;
		cmbBrch.Index = 0;
	}

    if ( sServiceName == "e05070030-service:searchBrchRtrn" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsOutBrchRtrn.rowcount);

        if(dsOutBrchRtrn.rowcount > 0){
            btnDelRow.Enable = true;
            divBtnLst.btnPrint.enable = true;
        }
    }

    if ( sServiceName == "e05070030-service:searchRacerRank" ) {
        if(dsOutBrchRtrn.rowcount > 0){
			if(dsOutRacerRank.rowcount > 0){
			   edtWinner.Text = dsOutRacerRank.GetColumn(0,"TMP")+" / "+
								dsOutRacerRank.GetColumn(1,"TMP")+" / "+
								dsOutRacerRank.GetColumn(2,"TMP");
			}

			edtCustName.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"CUST_NM");
			edtCustTel.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"CUST_TEL");
			edtCustAddr.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"CUST_ADDR");
			edtVehNo.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"VEH_NO");
			edtVehColor.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"VEH_COLOR");

			edtExaRate.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_RATE")+"배";
			edtExa1.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_WON1"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_NO1")+"매";
			edtExa2.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_WON2"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_NO2")+"매";
			edtExa3.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_WON3"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_NO3")+"매";               
			edtExa4.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_WON4"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_NO4")+"매";
			edtExa5.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_WON5"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"EXA_NO5")+"매"; 

			edtQuiRate.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_RATE")+"배";
			edtQui1.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_WON1"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_NO1")+"매";
			edtQui2.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_WON2"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_NO2")+"매";
			edtQui3.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_WON3"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_NO3")+"매";               
			edtQui4.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_WON4"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_NO4")+"매";
			edtQui5.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_WON5"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"QUI_NO5")+"매";  

			edtTriRate.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_RATE")+"배";
			edtTri1.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_WON1"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_NO1")+"매";
			edtTri2.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_WON2"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_NO2")+"매";
			edtTri3.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_WON3"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_NO3")+"매";               
			edtTri4.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_WON4"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_NO4")+"매";
			edtTri5.Text = NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_WON5"),0)+"원 X "+
						   dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TRI_NO5")+"매";  

			edtTotalReturn.Text = "총 환급금액 : "+NumFormat(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TOTAL_RETURN"),0)+"원";
			edtBuyPlc.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"BUY_PLC");  
			edtReturnPlc.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"RETURN_PLC"); 
			edtRmk.Text = dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"RMK");
			dsProgBar.SetColumn(0,"FILE_NM",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"FILE_NM"));
			dsProgBar.SetColumn(0,"FILE_PATH",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"FILE_PATH"));
			dsProgBar.SetColumn(0,"FILE_SIZE",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"FILE_SIZE"));

			if (!fnc_IsNull(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"PHOTO_PATH"))) {
				var ipUrl = replace(GBL_IMAGELINK_URL_FAIRNESS,"http://","");
				ipUrl = substr(ipUrl,0,indexOf(ipUrl,"/")); 
				var fileNm = replace(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"PHOTO_PATH"),"/WAS/web6/images/fairness/","");
				imgPhoto.ImageID = GBL_IMAGELINK_URL_FAIRNESS + fileNm ;
			} else imgPhoto.ImageID = "";
        } 
    }

    // 저장 완료 후
	if ( sServiceName == "e05070030-service:saveBrchRtrnDel" ) {
        bChanged = true;

	    fnc_Message(L_COM_ALT_0011, "N", "저장이");
	    fcSearch();
	}
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;

	var sServiceName = "e05070030-service:saveBrchRtrnDel";
	var sInDataSet   = "dsOutBrchRtrn=dsOutBrchRtrn:u";
	var sOutDataSet  = "";    

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSaveCheck(obj) {  
    if ( !fnc_DatasetChangeCheck(dsOutBrchRtrn) ) {
		    //저장 할 자료가 없음
		    fnc_Message(SNIS_COM_1006, "N");	
		    return false;
	    }

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {

	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {

		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
    	if ( IsExistVar("Arg_sRaceDay") && bChanged ) {
	    Close("Y");	//반환 후 조회
	} else {       
		Close();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  검색조건콤보 변경시 이벤트
+-------------------------------------------------------------------------------------------------*/
function cmbSearch_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex){
	if(cmbSearch.Value == "3"){
	    edtSearch.Text = "";
	    edtSearch.Number = true;
	}else{
	    edtSearch.Text = "";
	    edtSearch.Number = false;
	}
}

function dsOutBrchRtrn_OnRowPosChanged(obj,nOldRow,nRow){
	dsProgBar.SetColumn(0,"FILE_NM","");
	dsProgBar.SetColumn(0,"FILE_PATH","");
	dsProgBar.SetColumn(0,"FILE_SIZE","");
	dsProgBar.SetColumn(0,"PROG_BAR","");

	var sServiceName = "e05070030-service:searchRacerRank";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerRank=dsOutRacerRank";

    fce_AddParam("STND_YEAR", Trim(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"STND_YEAR")));
    fce_AddParam("TMS", Trim(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TMS")));
    fce_AddParam("DAY_ORD", Trim(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"DAY_ORD")));
    fce_AddParam("RACE_NO", Trim(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"RACE_NO")));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function btnDelRow_OnClick(obj){
	dsOutBrchRtrn.DeleteRow(dsOutBrchRtrn.Row);
}

function fcPrint(){
	dsOutBrchRtrnPrint.ClearData();
	dsOutBrchRtrnPrint.AddRow();

	dsOutBrchRtrnPrint.SetColumn(0,"STND_YEAR",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"STND_YEAR"));
	dsOutBrchRtrnPrint.SetColumn(0,"TMS",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"TMS"));
	dsOutBrchRtrnPrint.SetColumn(0,"DAY_ORD",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"DAY_ORD"));
	dsOutBrchRtrnPrint.SetColumn(0,"RACE_NO",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"RACE_NO"));

	dsOutBrchRtrnPrint.SetColumn(0,"CUST_NM",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"CUST_NM"));
	dsOutBrchRtrnPrint.SetColumn(0,"CUST_TEL",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"CUST_TEL"));
	dsOutBrchRtrnPrint.SetColumn(0,"CUST_ADDR",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"CUST_ADDR"));
	dsOutBrchRtrnPrint.SetColumn(0,"VEH_NO",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"VEH_NO"));
	dsOutBrchRtrnPrint.SetColumn(0,"VEH_COLOR",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"VEH_COLOR"));

	dsOutBrchRtrnPrint.SetColumn(0,"WINNER",edtWinner.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"EXA_RATE",edtExaRate.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"EXA1",edtExa1.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"EXA2",edtExa2.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"EXA3",edtExa3.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"EXA4",edtExa4.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"EXA5",edtExa5.Text);

	dsOutBrchRtrnPrint.SetColumn(0,"QUI_RATE",edtQuiRate.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"QUI1",edtQui1.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"QUI2",edtQui2.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"QUI3",edtQui3.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"QUI4",edtQui4.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"QUI5",edtQui5.Text);

	dsOutBrchRtrnPrint.SetColumn(0,"TRI_RATE",edtTriRate.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"TRI1",edtTri1.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"TRI2",edtTri2.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"TRI3",edtTri3.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"TRI4",edtTri4.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"TRI5",edtTri5.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"TOTAL_RETURN",edtTotalReturn.Text);

	dsOutBrchRtrnPrint.SetColumn(0,"BUY_PLC",edtBuyPlc.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"RETURN_PLC",edtReturnPlc.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"RMK",edtRmk.Text);
	dsOutBrchRtrnPrint.SetColumn(0,"INST_NM",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"INST_NM"));
	dsOutBrchRtrnPrint.SetColumn(0,"FILE_NM",dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"FILE_NM"));

	var fileNm = replace(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"PHOTO_PATH"),"/WAS/web6/images/fairness/","");
	dsOutBrchRtrnPrint.SetColumn(0,"IMG",GBL_IMAGELINK_URL_FAIRNESS + fileNm);

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";										//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e05070030_brch_rtrn.jrf";				//레포트 파일명
	var sDataSet  = "brch_rtrn=dsOutBrchRtrnPrint";			//"UBI 쿼리명, MIP Dataset명"

	var sArg = "";
	var sRptDrct  = 1;										//출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

function grdProgBar_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex){
	if(!fnc_IsNull(dsProgBar.GetColumn(0,"FILE_NM"))){
        var sLocalFileName  = fce_GetSelectFile("Save", dsProgBar.GetColumn(nRow, "FILE_NM"));
        var sServerFileName = dsProgBar.GetColumn(nRow, "FILE_PATH");
        var sFileSize       = dsProgBar.GetColumn(nRow, "FILE_SIZE");
        if ( sLocalFileName == null ) return;

        fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsProgBar);
    }
}

