			" USER_NM=" + quote(gdsUser.GetColumn(0,"USER_NM")) + 
			" DEPT_NM=" + quote(gdsUser.GetColumn(0,"DEPT_NM")) + 
			" STND_YEAR=" + quote(Trim(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"STND_YEAR"))) + 
			" SR_NO=" + quote(Trim(dsOutBrchRtrn.GetColumn(dsOutBrchRtrn.row,"SR_NO")));

    Dialog("form::E05070031.xml", sArgs, 1000, 514, "");
}
