//년도변경시
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
}


//  엑셀 버튼 클릭 時 처리
function fcToExcel(obj) {
	fce_OpenExcelListExport("grdSaleRslt:매출현황|grdRaceRslt:경주결과");   
}


// 조회이벤트
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e05110010-service:searchRaceSale";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSaleRslt=dsSaleRslt dsRaceRslt=dsRaceRslt dsSpecialDocu=dsSpecialDocu";
	var sMeet		 = "";

    if(cboSMbr.Value == "001") {
		sMeet = "003";
    }

	fce_AddParam("STND_YEAR"    , cboSStndYear.Value);
	fce_AddParam("MEET_CD"      , sMeet);
	fce_AddParam("MBR_CD"       , cboSMbr.Value);
	fce_AddParam("TMS"          , cboSRaceTms .Value);
	fce_AddParam("DAY_ORD"      , cboSDayOrd  .Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


// 조회버튼 클릭시 체크사항 처리
function fcSearchCheck(obj) {
	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }

    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 

    if(fnc_isNull(cboSRaceTms.Text)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }

    if(fnc_isNull(cboSDayOrd.Text)) {
            fnc_Message(SNIS_COM_1034, "N", "일차");
            cboSDayOrd.SetFocus();
            return false;
    }

    return true;
}


// 저장 이벤트
function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e05110010-service:saveCustomerGuess";
    var sInDataSet   = "dsRaceRslt=dsRaceRslt:u dsSpecialDocu=dsSpecialDocu:u";
    var sOutDataSet  = "";

   	fce_AddParam("STND_YEAR"    , cboSStndYear.Value);
	fce_AddParam("MBR_CD"       , cboSMbr.Value);
	fce_AddParam("TMS"          , cboSRaceTms .Value);
	fce_AddParam("DAY_ORD"      , cboSDayOrd  .Value);



	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", "U");
}


//저장 버튼 클릭시 처리
function fcSaveCheck(obj) {

	fcSearchCheck();

	if(length(tAreaSpecialDocu.Text) <= 0) {
		dsSpecialDocu.DeleteRow(0);
	}

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRaceRslt) && !fnc_DatasetChangeCheck(dsSpecialDocu))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	

		if(dsSpecialDocu.GetRowCount() <= 0) {
			dsSpecialDocu.AddRow();
		}

		return false;
	}

	return true;
}


// CallBack함수
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
		cboSMbr.Index = 0;
		fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
		return;
	}

		// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }

        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
		return;
	}

		// 일차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSDayOrd.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") 
          && cboSRaceTms.Value  == fce_GetCurRaceInfo("TMS") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", fce_GetCurRaceInfo("DAY_ORD"));
            if ( nIndexRow > -1 ) cboSDayOrd.Index = nIndexRow;
        }

		dsOutSDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", cboSDayOrd.Value);

		return;
	}

	// 경주상황보고 조회 후
	if ( sServiceName == "e05110010-service:searchRaceSale" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsRaceRslt.GetRowCount());

        if(dsSpecialDocu.GetRowCount() <= 0) {
			dsSpecialDocu.AddRow();
        }
         return;
	}

	// 경주상황보고 저장 후
	if ( sServiceName == "e05110010-service:saveCustomerGuess" ) {
		fnc_SetStatus(SNIS_BOA_0003, dsRaceRslt.GetRowCount());
		fcSearch(); 
        return;
	}
}


// 인쇄
function fcPrint(obj) {

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e05110010_race_result.jrf";        	//레포트 파일명
	var sDataSet  = "dsRaceRsltReport=dsRaceRslt dsSaleRsltReport=dsSaleRslt dsSpecialDocuReport=dsSpecialDocu";   	//"UBI 쿼리명, MIP Dataset명"
	var sArg  = " STND_YEAR=" + cboSStndYear.Value;
	    sArg += " MBR_CD="	  + cboSMbr.Value;	    
	    sArg += " TMS="		  + cboSRaceTms.value;
	    sArg += " DAY_ORD="	  + cboSDayOrd.value;
		sArg += " RACE_DATE=" + calRaceDt.Value;
		sArg += " IMG_URL="	  +logImgUrl;
	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

}



//회차변경시
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");	
}


//일차변경시
function cboSDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//경주일자셋팅
	dsOutSDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", cboSDayOrd.Value);
}

// 화면닫기 버튼
function fcEnd(obj) {
	Close();	
}
