//조회 
function fcSearch(obj) {
	fcSearchCheck();

    var ServiceName = "e06010035-service:searchPropeller";
    var sInDataSet  = "";
    var sOutDataSet = "dsOutPropeller=dsOutPropeller";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);

	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck( dsOutPropeller )  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	//oDataSet          = dsOutPropeller;
	//sMandatoryColID   = "PROPELLER1";
	//sMandatoryColName = "프로펠러번호";
	//oGrid             = "grdPropeller";
	//if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	//    return false;
	//}

	if ( !fcDatasetChk() ) return false;

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;

	var ServiceName = "e06010035-service:savePropeller";
    var sInDataSet   = "dsOutPropeller=dsOutPropeller:u";
    var sOutDataSet  = "dsOutPropeller=dsOutPropeller";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);

	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

