/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(ServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( ServiceName == "e06010035-service:searchPropeller" ) {
	    if(dsOutPropeller.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutPropeller.GetRowCount());
	    }

	}

	// 저장 후
	if ( ServiceName == "e06010035-service:savePropeller" ) {

	}

	//오류가 없이 정상 처리되었다면...
	fcPostProceS(ServiceName);
}

