//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

function dsOutPropeller_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "CHKPELLER1" || strColumnID == "CHKPELLER2" || strColumnID == "CHKPELLER3" || strColumnID == "CHKPELLER4" || strColumnID == "CHKPELLER5") {
		var nChk = toInteger(dsOutPropeller.GetColumn(nRow, "CHKPELLER1"))
		         + toInteger(dsOutPropeller.GetColumn(nRow, "CHKPELLER2"))
		         + toInteger(dsOutPropeller.GetColumn(nRow, "CHKPELLER3"))
		         + toInteger(dsOutPropeller.GetColumn(nRow, "CHKPELLER4"))
		         + toInteger(dsOutPropeller.GetColumn(nRow, "CHKPELLER5"));

		if ( (nChk > 3) || (nChk == 3 && varNewValue == "1") ) {
			fnc_Message(SNIS_BOA_F014, "N", "3");
			return false;
		}
	}
}

function fcDatasetChk(obj)
{
	var sPellerNoList;
	var strlist;

	var nRowCnt = dsOutPropeller.GetRowCount();
	for (var i = 0; i < nRowCnt; i++)
	{
		if (dsOutPropeller.GetRowType(i) == "update") {
			sPellerNoList = "";

			if ( dsOutPropeller.GetColumn(i, "CHKPELLER1") == "1" ) 
				sPellerNoList = sPellerNoList + dsOutPropeller.GetColumn(i, "RACER_PELLER1") + "|";
			if ( dsOutPropeller.GetColumn(i, "CHKPELLER2") == "1" ) 
				sPellerNoList = sPellerNoList + dsOutPropeller.GetColumn(i, "RACER_PELLER2") + "|";
			if ( dsOutPropeller.GetColumn(i, "CHKPELLER3") == "1" ) 
				sPellerNoList = sPellerNoList + dsOutPropeller.GetColumn(i, "RACER_PELLER3") + "|";
			if ( dsOutPropeller.GetColumn(i, "CHKPELLER4") == "1" ) 
				sPellerNoList = sPellerNoList + dsOutPropeller.GetColumn(i, "RACER_PELLER4") + "|";
			if ( dsOutPropeller.GetColumn(i, "CHKPELLER5") == "1" ) 
				sPellerNoList = sPellerNoList + dsOutPropeller.GetColumn(i, "RACER_PELLER5") + "|";

			strlist = Split(sPellerNoList,"|");
			dsOutPropeller.SetColumn(i, "PROPELLER1", strlist[0]);
			dsOutPropeller.SetColumn(i, "PROPELLER2", strlist[1]);
			dsOutPropeller.SetColumn(i, "PROPELLER3", strlist[2]);
		}
	}

	return true;
}
