function fcSave(obj) {

    var sServiceName = "e06010050-service:saveEquipInsp";
    var sInDataSet   = '';

    if(ORG_INSP_PRS_STAT_CD=='X'){
		sInDataSet = "dsOutEquipInsp=dsOutEquipInsp dsOutEquipInspDetail=dsOutEquipInspDetail:u";
	}else{
		sInDataSet = "dsOutEquipInsp=dsOutEquipInsp dsOutEquipInspDetail=dsOutEquipInspDetail:u";
	}
    var sOutDataSet  = "dsOutEquipInsp=dsOutEquipInsp dsOutEquipInspDetail=dsOutEquipInspDetail";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("DAY_ORD", cboDayOrd.Value);

	fce_AddParam("ORG_INSP_PRS_STAT_CD", ORG_INSP_PRS_STAT_CD);
	fce_AddParam("INSP_PRS_STAT_CD", dsOutEquipInsp.GetColumn(dsOutEquipInsp.rowposition, "INSP_PRS_STAT_CD"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

