/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06010050-service:searchEquipInsp" ) {
	    if(dsOutEquipInspDetail.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutEquipInspDetail.GetRowCount());
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);
	    }

	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

