//버는 Visible처리 
function fcSetButtonVisible(){
	var currPrsStatCd = dsOutEquipInsp.GetColumn(dsOutEquipInsp.rowposition, "INSP_PRS_STAT_CD");
	if((sInpt == "F") || IsExistVar("Arg_sStndYear")) {
		this.divBtnLst.btnSave.Visible = false;
		btnConfirm.Enable = false;
		btnConfirmCancel.Enable = false;
	} else {
		if(currPrsStatCd=="X" || currPrsStatCd=="001"){
			btnInspReport.Visible = true;
			btnConfirm.Visible = true;
			btnConfirmCancel.Visible = false;
			this.divBtnLst.btnSave.Visible = true;
			grdEquipInspDetail.Editable = true;
		}else if(currPrsStatCd=="002"){
			btnInspReport.Visible = true;
			btnConfirm.Visible = false;
			btnConfirmCancel.Visible = true;
			this.divBtnLst.btnSave.Visible = false;
			grdEquipInspDetail.Editable = false;
		}
	}
}

