//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sMbrCd") && bPopup ) {
		cboMbrCd.Value = Arg_sMbrCd;
	} else {       
		cboMbrCd.Value = CURR_USER_MBR_CD;
	}

	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//회차 DataSet Load 완료 시 
function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( IsExistVar("Arg_sTms") && bPopup ) {
		cboTms.Value = Arg_sTms;
	} else {       
		//cboTms.Value = CURR_TMS;
		cboTms.index = 0;
	}

	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value,  cboTms.Value, "dsOutDayOrd");
}

//일차 DataSetLoad 완료 시 
function dsOutDayOrd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsOutDayOrd.InsertRow(0);
	dsOutDayOrd.SetColumn(dsOutDayOrd.rowposition, "DAY_ORD", 0);

	if ( IsExistVar("Arg_sDayOrd") && bPopup ) {
		cboDayOrd.Value = Arg_sDayOrd;

		bPopup = false;
	} else {       
		if(CURR_RACE_DAY>CURR_DT){
			cboDayOrd.Value = CURR_DAY_ORD;
		}
	}

    fcSearch();
}

//합계표 계산 
function fcInspResultSum(){
	var dsCnt = dsOutEquipInspDetail.GetRowCount();
	var racerCnt = dsCnt;
	var racerFailCnt = 0;
	var racerSucessCnt  = 0;  

	var motCnt = dsCnt;
	var motFailCnt = 0;
	var motSucessCnt  = 0;  

	var boatCnt = dsCnt;
	var boatFailCnt = 0;
	var boatSucessCnt  = 0;  


	for(i=0; i<dsCnt; i++){
		var racerInspStat = dsOutEquipInspDetail.GetColumn(i, "RACER_INSP_STAT_CD");
		if(racerInspStat=="001"){
			racerSucessCnt ++;
		}else if(racerInspStat=="002"||racerInspStat=="003"){
			racerFailCnt ++;
		}

		var motInspStat = dsOutEquipInspDetail.GetColumn(i, "MOT_INSP_STAT_CD");
		if(motInspStat=="001"){
			motSucessCnt ++;
		}else if(motInspStat=="002" || motInspStat=="003"){
			motFailCnt ++;
		}

		var boatInspStat = dsOutEquipInspDetail.GetColumn(i, "BOAT_INSP_STAT_CD");
		if(boatInspStat=="001"){
			boatSucessCnt ++;
		}else if(boatInspStat=="002" || boatInspStat=="003"){
			boatFailCnt ++;
		}
	}


	dsOutEquipInsp.SetColumn(dsOutEquipInsp.rowposition, 'RACER_INSP_TARGET_CNT',dsCnt);
	edRacerInspCnt.Text = racerSucessCnt + racerFailCnt;
	edRacerInspPassCnt.Text = racerSucessCnt;
	edRacerInspFailCnt.Text = racerFailCnt;

	edMotInspTargetCnt.Text =  dsCnt;
	edMotInspCnt.Text = motSucessCnt + motFailCnt;
	edMotInspPassCnt.Text = motSucessCnt;
	edMotInspFailCnt.Text = motFailCnt;

	edBoatInspTargetCnt.Text =  dsCnt;
	edBoatInspCnt.Text = boatSucessCnt + boatFailCnt;
	edBoatInspPassCnt.Text = boatSucessCnt;
	edBoatInspFailCnt.Text = boatFailCnt;


}

//확정검사 결과가 변경된경우 
function dsOutEquipInspDetail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//선수가 미검사 인경우 Report 에서 해당 Row을 공백으로 처리한다. 
	if(strColumnID == "RACER_INSP_STAT_CD"  && varNewValue = '003'){
		dsOutEquipInspDetail.setColumn(dsOutEquipInspDetail.rowposition, 'MOT_INSP_STAT_CD', '003');
		dsOutEquipInspDetail.setColumn(dsOutEquipInspDetail.rowposition, 'BOAT_INSP_STAT_CD', '003');
	}	

	if(strColumnID == "RACER_INSP_STAT_CD" || strColumnID == "MOT_INSP_STAT_CD"  || strColumnID == "BOAT_INSP_STAT_CD" ){
	    //Summary재 계산
		if(varOldValue != varNewValue)fcInspResultSum();
	}
}

//확정검사 대상 DataSet 로드시 
function dsOutEquipInspDetail_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcInspResultSum();
}

