//레포트 DataSet으로 Copy
function fnCopyReportDataSet(){
	dsOutReportEquipInspDetail_1.ClearData();
	dsOutReportEquipInspDetail_2.ClearData();

	for(i=0; i<dsOutEquipInspDetail.GetRowCount(); i++){
		dsOutReportEquipInspDetail_1.AddRow();
		for(j=0; j<dsOutEquipInspDetail.GetColCount(); j++){
			var columnId = dsOutEquipInspDetail.GetColID(j);
			if(columnId == 'RACER_INSP_STAT_CD' || columnId == 'MOT_INSP_STAT_CD' || columnId == 'BOAT_INSP_STAT_CD'){
				dsOutReportEquipInspDetail_1.SetColumn(dsOutReportEquipInspDetail_1.rowposition , j, getStat(dsOutEquipInspDetail.GetColumn(i, j)));
			}else{
				dsOutReportEquipInspDetail_1.SetColumn(dsOutReportEquipInspDetail_1.rowposition , j, dsOutEquipInspDetail.GetColumn(i, j));
			}
		}

/*		
		//선수가 미검사 인경우 Report 에서 해당 Row을 공백으로 처리한다. 
		var racerInspStatCd =  dsOutEquipInspDetail.GetColumn(i, 'RACER_INSP_STAT_CD');
		if(racerInspStatCd=='003'){
			for(j=0; j<dsOutReportEquipInspDetail_1.GetColCount(); j++){
				var columnId = dsOutEquipInspDetail.GetColID(j);
				if(columnId=='SEQ' ) continue;
				dsOutReportEquipInspDetail_1.SetColumn(dsOutReportEquipInspDetail_1.rowposition , j, '');
			}
		}
*/		
		//if(i==29)break;  //60개 일때
		if(i==32)break;  //66개 일때
	}

	for(i=33; i<dsOutEquipInspDetail.GetRowCount(); i++){
		dsOutReportEquipInspDetail_2.AddRow();
		for(j=0; j<dsOutEquipInspDetail.GetColCount(); j++){
			var columnId = dsOutEquipInspDetail.GetColID(j);
			if(columnId == 'RACER_INSP_STAT_CD' || columnId == 'MOT_INSP_STAT_CD' || columnId == 'BOAT_INSP_STAT_CD'){
				dsOutReportEquipInspDetail_2.SetColumn(dsOutReportEquipInspDetail_2.rowposition , j, getStat(dsOutEquipInspDetail.GetColumn(i, j)));
			}else{
				dsOutReportEquipInspDetail_2.SetColumn(dsOutReportEquipInspDetail_2.rowposition , j, dsOutEquipInspDetail.GetColumn(i, j));
			}
		}
/*		
		//선수가 미검사 인경우 Report 에서 해당 Row을 공백으로 처리한다. 
		var racerInspStatCd =  dsOutEquipInspDetail.GetColumn(i, 'RACER_INSP_STAT_CD');
		if(racerInspStatCd=='003'){
			for(j=0; j<dsOutReportEquipInspDetail_1.GetColCount(); j++){
				var columnId = dsOutEquipInspDetail.GetColID(j);
				if(columnId=='SEQ' )continue;
				dsOutReportEquipInspDetail_1.SetColumn(dsOutReportEquipInspDetail_1.rowposition , j, '');
			}
		}
*/		
	}
}

function getStat(statCd){
	var stat = "";
	switch ( statCd) {
		case "001" :   //합격   
            stat = '○';
            break;
		case "002" : 	//불합격
            stat = '×';
            break;
        case "003" : 
			stat = '-';	//미검사 
			break;
     }
     return stat;
}

//확정 통보서 출력 
function fcEquipInspReportPrint(){

    fnCopyReportDataSet();


	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06019050_equip_insp_report.jrf";               //레포트 파일명
	var sDataSet  = "equip_insp=dsOutEquipInsp equip_insp_detail_1=dsOutReportEquipInspDetail_1 equip_insp_detail_2=dsOutReportEquipInspDetail_2";   //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "IMG_URL="+logImgUrl; 
	var dayOrd = cboDayOrd.Value;
	sArg += " DAY_ORD="+dayOrd;
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
