/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboTms, "회차")){
		return false;
   }
   return true;
}

function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

	var sServiceName = "e06010070-service:searchEquipLot";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEquipDrwlt=dsOutEquipDrwlt dsOutMotLot=dsOutMotLot dsOutBoatLot=dsOutBoatLot dsOutPropellerLot=dsOutPropellerLot"; 		//추첨 대상 보트 

    fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutMotLot ) 
	  && !fnc_DatasetChangeCheck(dsOUtBoatLot) 
	  && !fnc_DatasetChangeCheck(dsOUtPropellerLot)
	  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	return true;
}

//저장 
function fcSave(obj) {
    var stat = dsOutEquipDrwlt.GetColumn(dsOutEquipDrwlt.rowposition, "EQUIP_DRWLT_CMPL_YN");
    if(stat=='Y')dsOutEquipDrwlt.setColumn(dsOutEquipDrwlt.rowposition, "EQUIP_DRWLT_CMPL_YN", 'X');
    var sServiceName = "e06010070-service:saveEquipLot";
    var sInDataSet   = "dsOutEquipDrwlt=dsOutEquipDrwlt dsOutMotLot=dsOutMotLot:u dsOutBoatLot=dsOutBoatLot:u dsOutPropellerLot=dsOutPropellerLot:u";
    var sOutDataSet  = "dsOutEquipDrwlt=dsOutEquipDrwlt dsOutMotLot=dsOutMotLot dsOutBoatLot=dsOutBoatLot dsOutPropellerLot=dsOutPropellerLot";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTms.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

