function fcToExcel(obj) {
	if(tbLot.TabIndex ==0){
		fnc_OpenExcelExport("tbLot.tbMotLot.grdMotLot");
	}else if(tbLot.TabIndex ==1){
		fnc_OpenExcelExport("tbLot.tbBoatLot.grdBoatLot");
	}else{
		fnc_OpenExcelExport("tbLot.tbPropellerLot.grdPropellerLot");
	}
}

