/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06010070-service:searchEquipLot" ) {
	    setMotGridSize();
		setBoatGridSize();
	}

	// 저장 후
	if ( sServiceName == "e06010070-service:saveEquipLot" ) {

	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

