//확정 여부에 따른 버튼 visible처리 
function fcFormVisible(){
	var stat = dsOutEquipDrwlt.GetColumn(dsOutEquipDrwlt.rowposition, "EQUIP_DRWLT_CMPL_YN");
	var tmsStat = dsOutEquipDrwlt.GetColumn(dsOutEquipDrwlt.rowposition, "RACE_TMS_STAT_CD"); 
	if(tmsStat == null){
		rdTmsStatNm.Text = "회차가 등록 안됨";
		tmsStat = "XXXX"; //회차 상티를 없는것(예외)로 처리 
	}

	this.divBtnLst.btnSave.Visible = true;
	if(stat =="Y"){ //마감 이면 
		tbLot.tbMotLot.grdMotLot.Editable = true;
		tbLot.tbBoatLot.grdBoatLot.Editable = true;
		btnConfirm.Visible = false;
		btnConfirmCancel.Visible = true;
	}else{
		tbLot.tbMotLot.grdMotLot.Editable = true;
		tbLot.tbBoatLot.grdBoatLot.Editable = true;
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
	}

}


