//레포트 출력 버튼 클릭시 
function btnReport_OnClick(obj) {
	REPORT_LOT_ORD='0';
	http.sync=true;
	var nExcl_yn = dsOutMotLot.FindRow("EXCL_YN", "1");
	if (nExcl_yn >= 0) {
		EXCL_DESC = "(상위)";
		fcSearchReportEquipLostSummary(0, "1");
		fcSearchReportEquipLostDetail(0,  "1");
	} else {
		EXCL_DESC = "";
	}
	fcSearchReportEquipLostSummary(0, "0");
	fcSearchReportEquipLostDetail(0,  "0");
	http.sync=false;

}


//확정 통보서 출력 
function fcEquipLotPrint(){
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06019070_equip_lot.jrf";               //레포트 파일명
	var sDataSet  = "equip_lot_detail=dsOutReportEquipLotDetail equip_lot_summary=dsOutReportEquipLotSummary";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		sArg += " MBR_CD=" + cboMbrCd.Value;
		sArg += " TMS=" + cboTms.Value;
		sArg += " IMG_URL="+logImgUrl; 
		sArg += " REPORT_LOT_ORD=" + REPORT_LOT_ORD;
		sArg += " EXCL_DESC="+EXCL_DESC;
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

//레포트 장비 추첨 계 
function fcSearchReportEquipLostSummary(lotOrd, excl_yn) {
	if (fnc_isNull(excl_yn)) excl_yn = "0";
	fcSearchCheck();

	var sServiceName = "e06090070-service:searchReportEquipLotSummary";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutReportEquipLotSummary=dsOutReportEquipLotSummary"; 	

    fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_AddParam("MBR_CD",  cboMbrCd.Value);
    fce_AddParam("TMS",  cboTms.Value);
    fce_AddParam("LOT_ORD",  lotOrd);
    fce_AddParam("EXCL_YN",  excl_yn);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


//레포트 장비 추첨 대상 상세 현황 
function fcSearchReportEquipLostDetail(lotOrd, excl_yn) {
	if (fnc_isNull(excl_yn)) excl_yn = "0";
	fcSearchCheck();

	var sServiceName = "e06090070-service:searchReportEquipLotDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutReportEquipLotDetail=dsOutReportEquipLotDetail"; 

	var iMotCnt = ToInteger(dsOutReportEquipLotSummary.GetColumn(0, "MOT_CNT")/3) + Iif(dsOutReportEquipLotSummary.GetColumn(0, "MOT_CNT")%3 > 0, 1, 0);
	var iBoatCnt = ToInteger(dsOutReportEquipLotSummary.GetColumn(0, "BOAT_CNT")/3) + Iif(dsOutReportEquipLotSummary.GetColumn(0, "BOAT_CNT")%3 > 0, 1, 0);

    fce_AddParam("STND_YEAR", cboStndYear.Value);
	fce_AddParam("MBR_CD",  cboMbrCd.Value);
    fce_AddParam("TMS",  cboTms.Value);
    fce_AddParam("LOT_ORD",  lotOrd);
    fce_AddParam("EXCL_YN",  excl_yn);
    fce_AddParam("MOT_CNT",  Iif(iMotCnt >= iBoatCnt, iMotCnt, iBoatCnt));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



function dsOutReportEquipLotDetail_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcEquipLotPrint();
	if (EXCL_DESC != "") EXCL_DESC = "";
}


//  모터 추첨 대상 , 예비대상 동시 선택 방지 
function dsOutMotLot_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(varNewValue=='0')return;
	if(strColumnID == "LOT_YN" || strColumnID == "PREPAR_YN" || strColumnID == "PREPAR_ADD1_YN" || strColumnID == "PREPAR_ADD2_YN"){
		var stat = dsOutEquipDrwlt.GetColumn(dsOutEquipDrwlt.rowposition, "EQUIP_DRWLT_CMPL_YN");
		var lotYn = dsOutMotLot.GetColumn(nRow,"LOT_YN");
		var preparYn = dsOutMotLot.GetColumn(nRow,"PREPAR_YN");
		var preparAdd1Yn = dsOutMotLot.GetColumn(nRow,"PREPAR_ADD1_YN");
		var preparAdd2Yn = dsOutMotLot.GetColumn(nRow,"PREPAR_ADD2_YN");

		if(strColumnID == "LOT_YN"){
			dsOutMotLot.SetColumn(nRow,"PREPAR_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_ADD1_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_ADD2_YN", "0");
		}else if(strColumnID == "PREPAR_YN"){
			dsOutMotLot.SetColumn(nRow,"LOT_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_ADD1_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_ADD2_YN", "0");
		}else if(strColumnID == "PREPAR_ADD1_YN"){
			dsOutMotLot.SetColumn(nRow,"LOT_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_ADD2_YN", "0");
		}else if(strColumnID == "PREPAR_ADD2_YN"){
			dsOutMotLot.SetColumn(nRow,"LOT_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_YN", "0");
			dsOutMotLot.SetColumn(nRow,"PREPAR_ADD1_YN", "0");
		}

	}
}



// 보트 추첨 대상 , 예비 대상 동시 선태 방지 
function dsOutBoatLot_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(varNewValue=='0')return;
	if(strColumnID == "LOT_YN" || strColumnID == "PREPAR_YN" || strColumnID == "PREPAR_ADD1_YN" || strColumnID == "PREPAR_ADD2_YN"){
		var stat = dsOutEquipDrwlt.GetColumn(dsOutEquipDrwlt.rowposition, "EQUIP_DRWLT_CMPL_YN");
		var lotYn = dsOutBoatLot.GetColumn(nRow,"LOT_YN");
		var preparYn = dsOutBoatLot.GetColumn(nRow,"PREPAR_YN");
		var preparAdd1Yn = dsOutBoatLot.GetColumn(nRow,"PREPAR_ADD1_YN");
		var preparAdd2Yn = dsOutBoatLot.GetColumn(nRow,"PREPAR_ADD2_YN");

		if(strColumnID == "LOT_YN"){
			dsOutBoatLot.SetColumn(nRow,"PREPAR_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_ADD1_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_ADD2_YN", "0");
		}else if(strColumnID == "PREPAR_YN"){
			dsOutBoatLot.SetColumn(nRow,"LOT_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_ADD1_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_ADD2_YN", "0");
		}else if(strColumnID == "PREPAR_ADD1_YN"){
			dsOutBoatLot.SetColumn(nRow,"LOT_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_ADD2_YN", "0");
		}else if(strColumnID == "PREPAR_ADD2_YN"){
			dsOutBoatLot.SetColumn(nRow,"LOT_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_YN", "0");
			dsOutBoatLot.SetColumn(nRow,"PREPAR_ADD1_YN", "0");
		}

	}
}



