function buildStatReportDataSet()
{
	var lotTargetCnt = ToInteger(edLotTargetCnt.Text);
	var motCnt = dsOutMotLot.rowCount();
	var motRunableCnt = 0;
	var motReprCnt = 0;
	var motDepsCnt = 0;
	var motLotCnt = lotTargetCnt;

	var boatCnt = dsOutBoatLot.rowCount();
	var boatRunableCnt = 0;
	var boatReprCnt = 0;
	var boatDepsCnt = 0;
	var boatLotCnt = lotTargetCnt;

	for(i=0; i<motCnt; i++){
		var equipStatCd = dsOutMotLot.GetColumn(i, 'EQUIP_STAT_CD');
		if(equipStatCd=='002'){
			motReprCnt++;
		}else if(equipStatCd=='999'){
			motDepsCnt++;
		}
	}
	motRunableCnt = motCnt - motReprCnt - motDepsCnt;

	for(i=0; i<boatCnt; i++){
		var equipStatCd = dsOutBoatLot.GetColumn(i, 'EQUIP_STAT_CD');
		if(equipStatCd=='002'){
			boatReprCnt++;
		}else if(equipStatCd=='999'){
			boatDepsCnt++;
		}
	}
	boatRunableCnt = boatCnt - boatReprCnt - boatDepsCnt;


	//Summary정보 저장 
	dsOutEquipStatSummary.ClearData();
	dsOutEquipStatSummary.AddRow();

	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "STND_YEAR" , cboStndYear.Value);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "TMS" , cboTms.Value);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "CURR_DT" , CURR_DT);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "MOT_CNT" , motCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "MOT_RUNABLE_CNT" , motRunableCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "MOT_REPR_CNT" , motReprCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "MOT_DEPS_CNT", motDepsCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "MOT_LOT_CNT" , motLotCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "MOT_RMK" , '');
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "BOAT_CNT", boatCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "BOAT_RUNABLE_CNT", boatRunableCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "BOAT_REPR_CNT" , boatReprCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "BOAT_DEPS_CNT" , boatDepsCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "BOAT_LOT_CNT" , boatLotCnt);
	dsOutEquipStatSummary.SetColumn(dsOutEquipStatSummary.rowposition, "BOAT_RMK", '');


	var  mm = dsOutEquipStatSummary.getColumn(dsOutEquipStatSummary.rowposition, "STND_YEAR");
	//장비 상태 저장할 DataSet생성 
	var maxEquipCnt = 0;
	if(motCnt - boatCnt >0){
		maxEquipCnt = motCnt;
	}else{
		maxEquipCnt = boatCnt;
	}
	var dsPageCnt = 33;
	var dsRowCnt = Ceil(maxEquipCnt/3);
	var dsGroupCnt = Ceil(dsRowCnt/dsPageCnt);


	dsOutEquipStatDetail.ClearData();
	for(i=0; i<dsRowCnt; i++){
		dsOutEquipStatDetail.AddRow();
	}


	//모터 상태 저장 
	for(i=0; i<dsGroupCnt; i++){
		var remainRowCnt = dsRowCnt-dsPageCnt*i;  //나머지 row count
		if(remainRowCnt>=dsPageCnt)remainRowCnt = dsPageCnt; //loop count구하기 
		var basicIndex = dsPageCnt*i*3+ 1; //기준 Index
		for(j=0; j<remainRowCnt; j++){
		    var index = dsPageCnt*i + j;
		    var rowMotIndex = basicIndex + j + remainRowCnt*0;
		    if(rowMotIndex<motCnt+1){
				dsOutEquipStatDetail.SetColumn(index, "MOT_SEQ_1", rowMotIndex);
				dsOutEquipStatDetail.SetColumn(index, "MOT_NO_1", dsOutMotLot.GetColumn(rowMotIndex-1,'EQUIP_REG_NO'));
				dsOutEquipStatDetail.SetColumn(index, "MOT_STAT_1",  dsOutMotLot.GetColumn(rowMotIndex-1,'EQUIP_STAT_NM'));
			}

			rowMotIndex = basicIndex+ j + remainRowCnt*1;
			if(rowMotIndex<motCnt+1){
				dsOutEquipStatDetail.SetColumn(index, "MOT_SEQ_2", rowMotIndex);
				dsOutEquipStatDetail.SetColumn(index, "MOT_NO_2", dsOutMotLot.GetColumn(rowMotIndex-1,'EQUIP_REG_NO'));
				dsOutEquipStatDetail.SetColumn(index, "MOT_STAT_2",  dsOutMotLot.GetColumn(rowMotIndex-1,'EQUIP_STAT_NM'));
			}

			rowMotIndex = basicIndex+ j + remainRowCnt*2;
			if(rowMotIndex<motCnt+1){
				dsOutEquipStatDetail.SetColumn(index, "MOT_SEQ_3", rowMotIndex);
				dsOutEquipStatDetail.SetColumn(index, "MOT_NO_3", dsOutMotLot.GetColumn(rowMotIndex-1,'EQUIP_REG_NO'));
				dsOutEquipStatDetail.SetColumn(index, "MOT_STAT_3",  dsOutMotLot.GetColumn(rowMotIndex-1,'EQUIP_STAT_NM'));
			}
		}
	}

	//보트 상태 저장 
	for(i=0; i<dsGroupCnt; i++){
		var remainRowCnt = dsRowCnt-dsPageCnt*i;  //나머지 row count
		if(remainRowCnt>=dsPageCnt)remainRowCnt = dsPageCnt; //loop count구하기 
		var basicIndex = dsPageCnt*i*3+ 1; //기준 Index

		for(j=0; j<remainRowCnt; j++){
		    var index = dsPageCnt*i + j;
		    var rowBoatIndex = basicIndex + j + remainRowCnt*0;
		    if(rowBoatIndex<boatCnt+1){
				dsOutEquipStatDetail.SetColumn(index, "BOAT_SEQ_1", rowBoatIndex);
				dsOutEquipStatDetail.SetColumn(index, "BOAT_NO_1", dsOutBoatLot.GetColumn(rowBoatIndex-1,'EQUIP_REG_NO'));
				dsOutEquipStatDetail.SetColumn(index, "BOAT_STAT_1",  dsOutBoatLot.GetColumn(rowBoatIndex-1,'EQUIP_STAT_NM'));
			}

			rowBoatIndex = basicIndex+ j + remainRowCnt*1;
			if(rowBoatIndex<boatCnt+1){
				dsOutEquipStatDetail.SetColumn(index, "BOAT_SEQ_2", rowBoatIndex);
				dsOutEquipStatDetail.SetColumn(index, "BOAT_NO_2", dsOutBoatLot.GetColumn(rowBoatIndex-1,'EQUIP_REG_NO'));
				dsOutEquipStatDetail.SetColumn(index, "BOAT_STAT_2",  dsOutBoatLot.GetColumn(rowBoatIndex-1,'EQUIP_STAT_NM'));
			}
			rowBoatIndex = basicIndex+ j + remainRowCnt*2;
			if(rowBoatIndex<boatCnt+1){
				dsOutEquipStatDetail.SetColumn(index, "BOAT_SEQ_3", rowBoatIndex);
				dsOutEquipStatDetail.SetColumn(index, "BOAT_NO_3", dsOutBoatLot.GetColumn(rowBoatIndex-1,'EQUIP_REG_NO'));
				dsOutEquipStatDetail.SetColumn(index, "BOAT_STAT_3",  dsOutBoatLot.GetColumn(rowBoatIndex-1,'EQUIP_STAT_NM'));
			}
		}

	}

}

