/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setMotGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;

	//Alert("111 MAX_RACE_REG_NO : " + MAX_RACE_REG_NO);

	if(MAX_RACE_REG_NO <= 0 ) MAX_RACE_REG_NO = 6;

	tbLot.tbMotLot.grdMotLot.Redraw = false;

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        tbLot.tbMotLot.grdMotLot.SetColProp(14 + i, "Width", 50);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > MAX_RACE_REG_NO; i-- ) {
        tbLot.tbMotLot.grdMotLot.SetColProp(14 + i-1, "Width", 0);
    }

    tbLot.tbMotLot.grdMotLot.Redraw = true;
}


/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setBoatGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;

	//Alert("111 MAX_RACE_REG_NO : " + MAX_RACE_REG_NO);

	if(MAX_RACE_REG_NO <= 0 ) MAX_RACE_REG_NO = 6;

	tbLot.tbBoatLot.grdBoatLot.Redraw = false;

	var nCol0W = tbLot.tbBoatLot.grdBoatLot.GetColProp(0, "Width");
	var nCol1W = tbLot.tbBoatLot.grdBoatLot.GetColProp(1, "Width");
	var nCol2W = tbLot.tbBoatLot.grdBoatLot.GetColProp(2, "Width");
	var nCol3W = tbLot.tbBoatLot.grdBoatLot.GetColProp(3, "Width");
	var nCol4W = tbLot.tbBoatLot.grdBoatLot.GetColProp(4, "Width");
	var nCol5W = tbLot.tbBoatLot.grdBoatLot.GetColProp(5, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        tbLot.tbBoatLot.grdBoatLot.SetColProp(14 + i, "Width", 50);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > MAX_RACE_REG_NO; i-- ) {
        tbLot.tbBoatLot.grdBoatLot.SetColProp(14 + i-1, "Width", 0);
    }

    tbLot.tbBoatLot.grdBoatLot.SetColProp(0, "Width", nCol0W);
    tbLot.tbBoatLot.grdBoatLot.SetColProp(1, "Width", nCol1W);
    tbLot.tbBoatLot.grdBoatLot.SetColProp(2, "Width", nCol2W);
    tbLot.tbBoatLot.grdBoatLot.SetColProp(3, "Width", nCol3W);
    tbLot.tbBoatLot.grdBoatLot.SetColProp(4, "Width", nCol4W);
    tbLot.tbBoatLot.grdBoatLot.SetColProp(5, "Width", nCol5W);

    tbLot.tbBoatLot.grdBoatLot.Redraw = true;
}
function btnSelExcl_OnClick(obj)
{
	//if (dsOutMotLot.rowcount() == 0) return;
	tbLot.tbBoatLot.grdBoatLot.Redraw = false;
	tbLot.tbMotLot.grdMotLot.Redraw = false;

	dsOutMotLot.Sort("AVG_RANK_SCR", "false");
	dsOutBoatLot.Sort("AVG_RANK_SCR", "false");

	var iRow = 0;
	var iExclCnt = 0;
	var iExclMainMaxCnt = 0;
	var lret = false;

	//  추첨대상 상위수량
	iRow = dsExclCnt.FindRow("CD", "001");
	if (iRow >= 0) iExclMainMaxCnt = toNumber(dsExclCnt.GetColumn(iRow, "CD_NM"));

	// 예비장비 상위수량
	var iExclSpreMaxCnt = 0;
	iRow = dsExclCnt.FindRow("CD", "002");
	if (iRow > 0) iExclSpreMaxCnt = toNumber(dsExclCnt.GetColumn(iRow, "CD_NM"));


	for (iRow =0;iRow<dsOutMotLot.rowcount();iRow++) {
		dsOutMotLot.SetColumn(iRow, "EXCL_YN", "0");	
	}
	for (iRow =0;iRow<dsOutBoatLot.rowcount();iRow++) {
		dsOutBoatLot.SetColumn(iRow, "EXCL_YN", "0");	
	}

	//모터
	iExclCnt = 0;
	for (iRow =0;iRow<dsOutMotLot.rowcount();iRow++) {
		if (dsOutMotLot.GetColumn(iRow, "LOT_YN") == "1") {
			if (iExclCnt < iExclMainMaxCnt) {
				lret = dsOutMotLot.SetColumn(iRow, "EXCL_YN", "1");
				iExclCnt ++;
			}
		}
	}
	//보트
	iExclCnt = 0;
	for (iRow =0;iRow<dsOutBoatLot.rowcount();iRow++) {
		if (dsOutBoatLot.GetColumn(iRow, "LOT_YN") == "1") {
			if (iExclCnt < iExclMainMaxCnt) {
				dsOutBoatLot.SetColumn(iRow, "EXCL_YN", "1");
				iExclCnt ++;
			}
		}
	}


	//모터
	iExclCnt = 0;
	for (iRow =0;iRow<dsOutMotLot.rowcount();iRow++) {
		if (dsOutMotLot.GetColumn(iRow, "PREPAR_YN") == "1") {
			if (iExclCnt < iExclSpreMaxCnt) {
				dsOutMotLot.SetColumn(iRow, "EXCL_YN", "1");
				iExclCnt ++;
			}
		}
	}
	//보트
	iExclCnt = 0;
	for (iRow =0;iRow<dsOutBoatLot.rowcount();iRow++) {
		if (dsOutBoatLot.GetColumn(iRow, "PREPAR_YN") == "1") {
			if (iExclCnt < iExclSpreMaxCnt) {
				dsOutBoatLot.SetColumn(iRow, "EXCL_YN", "1");
				iExclCnt ++;
			}
		}
	}


	dsOutMotLot.Sort("EQUIP_REG_NO");
	dsOutBoatLot.Sort("EQUIP_REG_NO");

	tbLot.tbBoatLot.grdBoatLot.Redraw = true;
	tbLot.tbMotLot.grdMotLot.Redraw = true;
}


function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	// 넓이 조절
	var nWidth = nX;
	var nWidthGrd = nWidth;
	if( nX <= 1014){
		nWidth = 1002;
	} else {
		nWidth = nX-12;
	}	
	divBtnLst.left = nX-358;
	Shape0.Width = nWidth;		
	nWidthGrd = nWidth-8;	
	tbLot.width = nWidth;
	tbLot.tbMotLot.grdMotLot.Width = nWidthGrd;
	tbLot.tbBoatLot.grdBoatLot.Width = nWidthGrd;
	tbLot.tbPropellerLot.grdPropellerLot.Width = nWidthGrd;

	//높이 조절
	var nHeight = nY;
	var nHeightGrd = nHeight - 48;
	if (nY <= 590) {
		nHeight = 518;		
	} else {	
		nHeight = nY - (tbLot.Top +2);
	}
	nHeightGrd = nHeight - 48;
	tbLot.Height = nHeight;
	tbLot.tbMotLot.grdMotLot.Height = nHeightGrd;
	tbLot.tbBoatLot.grdBoatLot.Height = nHeightGrd;
	tbLot.tbPropellerLot.grdPropellerLot.Height = nHeightGrd;

}
