/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");        

        fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
		return;
	}

    // 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms1.Index = 0;
        cboSRaceTms2.Index = 0;
        if ( cboSStndYear.Value == CURR_STND_YEAR ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", CURR_TMS);
            if ( nIndexRow > -1 ) cboSRaceTms1.Index = nIndexRow;
            if ( nIndexRow > -1 ) cboSRaceTms2.Index = nIndexRow;
        }       

		return;
	}    

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  년도, 분기 변경시
+-------------------------------------------------------------------------------------------------*/
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();
    fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
}

function pivotUsermin(currow)
{
	var varPivotGrid = grdOutWinRatioPivot;	//피봇그리드명
	var varPivotDs = dsOutWinRatioPivot;	//피봇데이타셋명
	var pivotColNm = "MIN_ITRDT_RUN_TM";	//피봇데이타셋의 min값을 구할 컬럼명
	var minValue;							//min값
	var iCnt = varPivotGrid.GetPivotIndexCount();
	var varArray = Array();

	for(arr=0,j=0;j<iCnt;j++) {
		if(varPivotDs.getColumn(currow,pivotColNm, j) != null) {
			varArray[arr] = varPivotDs.getColumn(currow,pivotColNm, j);
			arr++;
		}
	} 
	for(i=0;i<varArray.length;i++) {
		if(i==0) minValue = varArray[i];
		else {
			if(minValue > varArray[i]) {
				minValue = varArray[i];
			}
		}
	}
	return minValue;
}  

function grdOutWinRatioPivot_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
		var sMot = dsOutWinRatioPivot.GetColumn(nRow, "MOT_NO");

		if ( fnc_IsNull(sMot) ) return;
		//dsOutWinRatioGraph.Filter("MOT_NO= '"+sMot+"'");
		dsOutWinRatioGraph.Sort("MOT_NO:A,TMS:D,GBN:A");
		var sArg = "Arg_sMot=" + quote(sMot);
		Dialog("form::E06010120.xml", sArg, 0, 0, true,0,0);
}
