/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {

   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(calIjrySdt, "시작일자")){
		return false;
   }else if(!fnc_IsMandatory(calIjryEdt, "종료일자")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}


    var sServiceName = "e06010140-service:searchPropella";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPropellaList=dsOutPropellaList";       

    fce_AddParam("MBR_CD", cboMbrCd.Value);

    fce_AddParam("CHK_NOT_REG", chkNotReg.Value);
    fce_AddParam("CHK_NOT_DISUSE", chkNotDisuse.Value);

    fce_AddParam("SEARCH_GUBUN", cboSearchGubun.Value);

    fce_AddParam("SEARCH_SDT", calIjrySdt.Value);
    fce_AddParam("SEARCH_EDT", calIjryEdt.Value);

    fce_AddParam("SEARCH_MOT_NO", edMotNoSearch.Value);

    fce_AddParam("REG_TYPE_CD", cboRegTypeCd.Value);
    fce_AddParam("DISUSE_TYPE_CD", cboDisuseTypeCd.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutPropellaList ) )
	{
		if ( !fnc_DatasetChangeCheck(dsOutMaintenance )  )
		{//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}
	}



	// 필수항목체크
	oDataSet          = dsOutPropellaList;
	sMandatoryColID   = "MBR_CD|PROPELLER_NO|MOT_NO";
	sMandatoryColName = "경정장|프로펠러번호|모터번호";
	oGrid             = grdPropellaList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	var sWork = "PROP";

	for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetColumn(i, "REG_PTO_UPDATE1") == "Y" ) {
			var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, oDataSet.GetColumn(i, "REG_PRO_PHO_PATH1") + "\\" + oDataSet.GetColumn(i, "REG_PRO_PHO_NM1"), 
															oDataSet.GetColumn(i, "REG_PRO_PHO_NM1"),
															sWork);
			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}

			var sTemp = Replace(rtn_arr[1],"\\","/");
			var asFile = split(sTemp, "/");

			oDataSet.SetColumn(i, "REG_PRO_PHO_PATH1", asFile[asFile.length - 1]);
        }

        if ( oDataSet.GetColumn(i, "REG_PTO_UPDATE2") == "Y" ) {
			var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, oDataSet.GetColumn(i, "REG_PRO_PHO_PATH2") + "\\" + oDataSet.GetColumn(i, "REG_PRO_PHO_NM2"), 
															oDataSet.GetColumn(i, "REG_PRO_PHO_NM2"),
															sWork);

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}

			var sTemp = Replace(rtn_arr[1],"\\","/");
			var asFile = split(sTemp, "/");

			oDataSet.SetColumn(i, "REG_PRO_PHO_PATH2", asFile[asFile.length - 1]);
        }

        if ( oDataSet.GetColumn(i, "DISUSE_PTO_UPDATE1") == "Y" ) {
			var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, oDataSet.GetColumn(i, "DISUSE_PRO_PHO_PATH1") + "\\" + oDataSet.GetColumn(i, "DISUSE_PRO_PHO_NM1"), 
															oDataSet.GetColumn(i, "DISUSE_PRO_PHO_NM1"),
															sWork);

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}

			var sTemp = Replace(rtn_arr[1],"\\","/");
			var asFile = split(sTemp, "/");

			oDataSet.SetColumn(i, "DISUSE_PRO_PHO_PATH1", asFile[asFile.length - 1]);
        }

        if ( oDataSet.GetColumn(i, "DISUSE_PTO_UPDATE2") == "Y" ) {
			var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, oDataSet.GetColumn(i, "DISUSE_PRO_PHO_PATH2") + "\\" + oDataSet.GetColumn(i, "DISUSE_PRO_PHO_NM2"), 
															oDataSet.GetColumn(i, "DISUSE_PRO_PHO_NM2"),
															sWork);

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}

			var sTemp = Replace(rtn_arr[1],"\\","/");
			var asFile = split(sTemp, "/");

			oDataSet.SetColumn(i, "DISUSE_PRO_PHO_PATH2", asFile[asFile.length - 1]);
        }
    }

	return true;
}


function fcSave(obj) {
    if ( !fcSaveCheck() ) return;   

    var sServiceName = "e06010140-service:savePropellerMgr";
    var sInDataSet   = "dsOutPropellaList=dsOutPropellaList:u";
    var sOutDataSet  = "dsOutPropellaList=dsOutPropellaList";

    fce_AddParam("MBR_CD",         cboMbrCd.Value);    
    fce_AddParam("CHK_NOT_REG",    chkNotReg.Value);
    fce_AddParam("CHK_NOT_DISUSE", chkNotDisuse.Value);    
    fce_AddParam("SEARCH_GUBUN",   cboSearchGubun.Value);    
    fce_AddParam("SEARCH_SDT",     calIjrySdt.Value);
    fce_AddParam("SEARCH_EDT",     calIjryEdt.Value);    
    fce_AddParam("SEARCH_NM_KOR",  edMotNoSearch.Value);    
    fce_AddParam("REG_TYPE_CD",    cboRegTypeCd.Value);
    fce_AddParam("DISUSE_TYPE_CD", cboDisuseTypeCd.Value);

    //지급펠러번호
    fce_AddParam("PROVIDE_PROPELLER_NO", Trim(edProvidePro.Text));

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//cboMbrCd.Value = CURR_USER_MBR_CD;
	if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
    }

    dsOutMaintenance.ClearData();
	dsOutPropellaList.AddRow();  

	var mbrcd =cboMbrCd.Value;

	dsOutPropellaList.SetColumn(dsOutPropellaList.rowposition,"MBR_CD",mbrcd);

	for(var i = 1 ; i <= dsOutPropellaList.GetRowCount(); i++) {
		dsOutPropellaList.SetColumn(dsOutPropellaList.rowposition,"NUM",i);		
	}	

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if(dsOutPropellaList.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003);		
	}

	dsOutPropellaList.DeleteRow(dsOutPropellaList.Row);
	for(var i = 1 ; i <= dsOutPropellaList.GetRowCount(); i++) {
		dsOutPropellaList.SetColumn(dsOutPropellaList.rowposition,"NUM",i);
	}	
}


