/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {

		dsOutRegTypeCd_0.Copy(dsOutRegTypeCd);
		dsOutDisuseTypeCd_0.Copy(dsOutDisuseTypeCd);

		fce_AddCodeRow(dsOutRegTypeCd, "전체", "", cboRegTypeCd, false);
		fce_AddCodeRow(dsOutDisuseTypeCd, "전체", "", cboDisuseTypeCd, false);

		fce_AddCodeRow(dsOutRegTypeCd_0, "선택", "", cboRegTypeCd_0, false);
		fce_AddCodeRow(dsOutDisuseTypeCd_0, "선택", "", cboDisuseTypeCd_0, false);

	// 프로펠러 번호 중복 체크 
	}else if ( sServiceName == "e06010140-service:checkPropellaNo" ) {
		if(toNumber(dsOutChkProDup.GetColumn(0, "DUPLICATION")) > 0){
			alert("중복된 프로펠러 번호 입니다. 다시 입력하세요.");
			edProNo.Text = preProNo;
			edProNo.SetFocus();
		}
	}else if ( sServiceName == "e06010140-service:searchDisusePropella" ){

		var nRow = 0;

		dsOutDisusePropeller.InsertRow(nRow);
		dsOutDisusePropeller.SetColumn(nRow, "PROPELLER_NO_NM", "반납없음");
		dsOutDisusePropeller.SetColumn(nRow, "PROPELLER_NO"   , ""  );
	//지급 프로펠러 번호 존재 유무 확인	
	}else if ( sServiceName == "e06010140-service:chkProvidePro" ){

		if(toNumber(dsOutChkProvide.GetColumn(0, "EXSIT")) > 0){
			alert("프로펠러 목록에 없는 번호이거나, 다른 선수에게 등록된 번호입니다.");
			edProvidePro.Text = "";
			edProvidePro.SetFocus();
		}
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


