//주문내역서 출력 조회 
function fcPrintSearch(){
	var orderCnt =0;
    var unitPrice =0;
    var totalSum =0;
	for(var i = 0 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
		unitPrice += ToInteger(dsOutOrderPartsList.GetColumn(i, "UNIT_PRICE"));
		orderCnt += ToInteger(dsOutOrderPartsList.GetColumn(i, "ORDER_CNT"));
		totalSum += ToInteger(dsOutOrderPartsList.GetColumn(i, "AMOUNT"));		
	}

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06029030_order_list.jrf";               //레포트 파일명
	var sDataSet  = "dsOutOrderPartsList=dsOutOrderPartsList";    //"UBI 쿼리명, MIP Dataset명"

	var sArg  = "PARTS_COM_CD="+dsOutOrderList.getColumn(dsOutOrderList.currow, "COM_NM");
		sArg += " ORDER_DT="+dsOutOrderList.getColumn(dsOutOrderList.currow, "ORDER_DT"); 	
		sArg += " IMG_URL="+logImgUrl; 
		sArg += " UNIT_PRICE="+unitPrice; 
		sArg += " ORDER_CNT="+orderCnt; 
		sArg += " TOTAL_SUM="+totalSum; 

	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

}



/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {

	var sFormName   = "";    //호출할 Form ID
	var sInputValue = "";    //입력된 자료가 있다면 Default로 조회 
    var sResponse;           //Popup에서 받을 결과 값

	switch(obj.id) {

		case "imgRegProPho1" :   //사진
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "REG_PTO_UPDATE1"    , "Y");
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "REG_PRO_PHO_PATH1"  , sResponse[0]);
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "REG_PRO_PHO_NM1"    , sResponse[1]);
				edRegProPho1.Text = sResponse[1];	
			}
			break;	

		case "imgRegProPho2" :   //사진
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "REG_PTO_UPDATE2"    , "Y");
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "REG_PRO_PHO_PATH2"  , sResponse[0]);
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "REG_PRO_PHO_NM2"    , sResponse[1]);
				edRegProPho2.Text = sResponse[1];	
			}
			break;	

		case "imgDisuseProPhoNM1" :   //사진
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "DISUSE_PTO_UPDATE1"    , "Y");
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "DISUSE_PRO_PHO_PATH1"  , sResponse[0]);
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "DISUSE_PRO_PHO_NM1"    , sResponse[1]);
				edDisuseProPhoNM1.Text = sResponse[1];	
			}
			break;	

		case "imgDisuseProPhoNM2" :   //사진
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "DISUSE_PTO_UPDATE2"    , "Y");
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "DISUSE_PRO_PHO_PATH2"  , sResponse[0]);
				dsOutPropellaList.SetColumn(dsOutPropellaList.currow, "DISUSE_PRO_PHO_NM2"    , sResponse[1]);
				edDisuseProPhoNM2.Text = sResponse[1];	
			}
			break;	
	}
}


