var preProNo = "";

function edProNo_OnFocus(obj)
{
	preProNo = obj.value;
}

function edProNo_OnKillFocus(obj)
{

	if(preProNo == obj.value) return false;

	if(fnc_IsNull(edProNo.Value)) return false;


	var sServiceName = "e06010140-service:checkPropellaNo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutChkProDup=dsOutChkProDup";       

    fce_AddParam("PROPELLER_SEQ", dsOutPropellaList.GetColumn(dsOutPropellaList.currow, "PROPELLER_SEQ"));
    fce_AddParam("PROPELLER_NO", edProNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  반납 프로펠러 조회.
+-------------------------------------------------------------------------------------------------*/

function searchDisuseList(){
	var sServiceName = "e06010140-service:searchDisusePropella";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDisusePropeller=dsOutDisusePropeller";
		sOutDataSet += " dsOutMaintenance=dsOutMaintenance";

    fce_AddParam("MOT_NO", edMotNo.Text);
    fce_AddParam("PROPELLER_SEQ", dsOutPropellaList.GetColumn(dsOutPropellaList.currow, "PROPELLER_SEQ"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function common_OnChanged(obj,strText)
{
	alert("common_OnChanged");
	searchDisuseList();
}

/*-------------------------------------------------------------------------------------------------+
>> 지급 프로펠러 visible
+-------------------------------------------------------------------------------------------------*/
function cboDisuseTypeCd_0_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	setVisibleprovidePeller();
	setVisibleDamagePart();
}

function setVisibleprovidePeller(){
	if(cboDisuseTypeCd_0.value == "300"){ //파손교체반납
		stcProvidePro.Visible = true;
		edProvidePro.Visible = true;
	}else{
		stcProvidePro.Visible = false;
		edProvidePro.Visible = false;
	}
}

function setVisibleDamagePart(){
	if(cboDisuseTypeCd_0.value == "200"){ //개인등록취소요청
		stcDamagePart.Visible = true;
		edDamagePart.Visible = true;
	}else{
		stcDamagePart.Visible = false;
		edDamagePart.Visible = false;
	}
}

/*-------------------------------------------------------------------------------------------------+
>> 프로펠러 목록중 로우 선택시.
+-------------------------------------------------------------------------------------------------*/
function dsOutPropellaList_OnRowPosChanged(obj,nOldRow,nRow)
{
	setVisibleprovidePeller();
	setVisibleDamagePart();
	searchDisuseList();

	this.imgPhotoNm.ImageID = "";
    var sPtoName = dsOutPropellaList.GetColumn(nRow, "DISUSE_PRO_PHO_PATH1");

    if ( !fnc_IsNull(sPtoName) ) {
    	//var sPhysicalFileName = split(sPtoName, "\\");
    	var sPhysicalFileName = split(sPtoName, "//");
    	var url = GBL_IMAGELINK_URL + strFolder + "/" + sPhysicalFileName[length(sPhysicalFileName) - 1];

		this.imgPhotoNm.ImageID = url;
		imgPhotoNm.FillType = "STRETCH";
	}
}

/*-------------------------------------------------------------------------------------------------+
>> 지급 프로펠러 존재 확인.
+-------------------------------------------------------------------------------------------------*/
function edProvidePro_OnKillFocus(obj)
{
	if(fnc_IsNull(edProvidePro.Value)) return false;

	var sServiceName = "e06010140-service:chkProvidePro";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutChkProvide=dsOutChkProvide";       


    fce_AddParam("PROPELLER_NO", edProvidePro.Text);
    fce_AddParam("MOT_NO", edMotNo.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


