	exeImgDown("4");
}

//이미지 다운로드.
function exeImgDown(gubun){

	var strPhoNm = "";
	var strPhoPath = "";

	if(gubun == "3"){
		strPhoNm = "DISUSE_PRO_PHO_NM1";
		strPhoPath = "DISUSE_PRO_PHO_PATH1";
	}else if(gubun == "4"){
		strPhoNm = "DISUSE_PRO_PHO_NM2";
		strPhoPath = "DISUSE_PRO_PHO_PATH2";
	}


	var sLocalFileName  = "";
    var rtn = fn_Msg_Box();
    if(rtn == 8){
		sLocalFileName = [strFilePath, dsOutPropellaList.GetColumn(dsOutPropellaList.row, strPhoNm)];
    }else if(rtn == 9){
		sLocalFileName  = fce_GetSelectFile("Save", dsOutPropellaList.GetColumn(dsOutPropellaList.row, strPhoNm));
    }else{
        return false;
    }

    // alert(sLocalFileName[0]+"/"+sLocalFileName[1]);
    var sServerFileName = "/WAS/web6/images/" + strFolder + "/" + dsOutPropellaList.GetColumn(dsOutPropellaList.row, strPhoPath);

    if ( sLocalFileName == null ) return;

	fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName);

    if( rtn == 8){
		ExecShell(sLocalFileName[0] + "\\" + sLocalFileName[1]);
	}

}

function fn_Msg_Box()
{
	var args  = "btn_style=" + Quote("MB_HONGJO");
		args += " str_msg=" + Quote("실행 또는 파일을 다운로드를 선택해 주세요");
		args += " title=" + Quote("파일실행/다운로드");
	var rtn = dialog("common::comMsgBoxPop.xml", args);
	return rtn;
}

//다운로드 임시 폴더 생성.
function fcDirectoryCreate(){

	var nFileCount = divBtnLst.fldImage.GetFileList(Dataset0,strFilePath, "*.*" );
	if( nFileCount = -1 ) 
	{
		divBtnLst.flImage.MakeDir(strFilePath);

	}
}


function fcedOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		fcSearch();
	}
}
