//입고일련번호
function fcEntSeq(){
	var sServiceName = "e06020040-service:searchEntSeq";
	var sInDataSet   = "";
	var sOutDataSet  = "dsOutEntSeq=dsOutEntSeq dsOutNextEntSeq=dsOutNextEntSeq";

	dsOutEntSeq.ClearData();
	dsOutNextEntSeq.ClearData();
