function fcInit(obj) {	
	if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
	dsOutOrder.ClearData();
	dsOutEnt.ClearData();
	dsOutEntSum.ClearData();

	fcEntSeq();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}    
    var sServiceName = "e06020040-service:searchEnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrder=dsOutOrder dsOutEnt=dsOutEnt dsOutEntSum=dsOutEntSum";       
    var orderDt ="";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    if(cboOrderDt.value =='' || cboOrderDt.value ==NULL){		
    }else{
		orderDt = SubStr(cboOrderDt.value,0,4) +SubStr(cboOrderDt.value,5,2)+SubStr(cboOrderDt.value,8,2);		
    }
    fce_AddParam("ORDER_DT", orderDt);
    fce_AddParam("PARTS_COM_CD", cboPartsComCd.Value);	
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if(dsOutOrder.GetRowCount()==0){
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;		
	}	
	// 필수항목체크
	oDataSet          = dsOutOrder;
	sMandatoryColID   = "STND_YEAR|MBR_CD|ORDER_SEQ";
	sMandatoryColName = "년도|경정장|주문번호";
	oGrid             = grdOutOrder;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;

}

function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	//입고 상태 변경 
	var orderCnt =0;
	var entCnt =0;
	for(var i = 0 ; i <= dsOutEnt.GetRowCount(); i++) {
		orderCnt += ToInteger(dsOutEnt.GetColumn(i, "ORDER_CNT"));
		entCnt += ToInteger(dsOutEnt.GetColumn(i, "SUM_CNT"));

	}	
	var entState = "";
	if(orderCnt ==entCnt){
		entState ="003";
	}else if(entCnt >0){
		entState ="002";
	}else{
		entState ="001";
	}
	dsOutOrder.setColumn(dsOutOrder.rowposition, "ENT_STATE", entState);


    fce_AddParam("STND_YEAR", dsOutEnt.getColumn(0, "STND_YEAR"));	
    fce_AddParam("MBR_CD", dsOutEnt.getColumn(0, "MBR_CD"));
    //fce_AddParam("ENT_SEQ", dsOutEnt.getColumn(0, "ENT_SEQ"));
    fce_AddParam("ENT_SEQ", edEntSeq.Text);
    fce_AddParam("NEXT_ENT_SEQ", edNextEntSeq.Text);
    fce_AddParam("ENT_DT", calIjrySdt.Value);

    var sServiceName = "e06020040-service:saveEnt";
    var sInDataSet   = "dsOutOrder=dsOutOrder:u dsOutEnt=dsOutEnt:u";
    var sOutDataSet  = "dsOutEnt=dsOutEnt dsOutOrder=dsOutOrder";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


