/*-------------------------------------------------------------------------------------------------+
>>  인쇄 (부품입고내역서)
+-------------------------------------------------------------------------------------------------*/
function fcCheckPrint(obj) {
	var giEntCnt =0;
    var unitPrice =0;
    var totalSum =0;
	for(var i = 0 ; i <= dsOutEnt.GetRowCount(); i++) {
		unitPrice += ToInteger(dsOutEnt.GetColumn(i, "UNIT_PRICE"));
		giEntCnt += ToInteger(dsOutEnt.GetColumn(i, "GI_ENT_CNT"));
		totalSum += ToInteger(dsOutEnt.GetColumn(i, "UNIT_PRICE")) * ToInteger(dsOutEnt.GetColumn(i, "GI_ENT_CNT"));		
	}	

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06029040_ent_list.jrf";               //레포트 파일명
	var sDataSet  = "dsOutEnt=dsOutEnt";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+dsOutEntDt.getColumn(dsOutEntDt.rowposition,"ENT_DT")	;
		sArg += " DAY_NM="+getDay(dsOutEntDt.getColumn(dsOutEntDt.rowposition,"ENT_DT"));
		sArg += " PARTS_COM_CD="+dsOutOrder.getColumn(grdOutOrder.clickedRow, "COM_NM")	; 	
		sArg += " IMG_URL="+logImgUrl; 
		sArg += " UNIT_PRICE="+unitPrice; 
		sArg += " ENT_CNT="+giEntCnt; 
		sArg += " TOTAL_SUM="+totalSum; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06020040-service:searchEnt" ) {
		if(dsOutOrder.GetRowCount()>0){
			//fcGetSpecCode(0);
		}else{
			dsOutEnt.ClearData();
			dsOutEntSum.ClearData();
		}
	    if(dsOutEnt.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutEnt.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);			
	    }
	    fcEntSeq();

	}else if(sServiceName == "e06020040-service:searchOrderDt"){
		if(dsOutOrderDt.GetRowCount()>0){
			fce_AddCodeRow(dsOutOrderDt, '전체','','');	
			cboOrderDt.Value = '';			
		}else{			
		}
	}
	else if(sServiceName == "e06020040-service:searchEntSeq") {
		if(dsOutEntSeq.rowcount == 0){
			edEntSeq.Text = "";
		}
		else
		{
			edEntSeq.Text = dsOutEntSeq.GetColumn(0,"ENT_SEQ");
		}
		edNextEntSeq.Text = dsOutNextEntSeq.GetColumn(0,"NEXT_ENT_SEQ");
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

