// 입고 수량 변경시 
function dsOutEnt_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if(strColumnID =="ENT_CNT"){
		var orderCnt = ToInteger(dsOutEnt.GetColumn(nRow, "ORDER_CNT"));
		var giEntCnt = ToInteger(dsOutEnt.GetColumn(nRow, "GI_ENT_CNT"));

		if(giEntCnt==''||giEntCnt==null)giEntCnt=0;

		var entCnt = ToInteger(dsOutEnt.GetColumn(nRow, "ENT_CNT"));
		var remain = (orderCnt- giEntCnt);		

		if(remain < entCnt){
			dsOutEnt.SetColumn(nRow,"ENT_CNT",varOldValue);	
			fnc_Message(SNIS_BOA_F004,"N");
		}else{
			dsOutEnt.SetColumn(nRow,"SUM_CNT",giEntCnt+entCnt);	
		}	


	}
}


