function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fce_AddCodeRow(dsOutMbrCd, '전체', '', '');
	cboMbrCd.Value = CURR_USER_MBR_CD;
}

function dsOutParts_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID =="NOW_UNIT_CNT"){
		var amount = dsOutParts.GetColumn(nRow, "NOW_UNIT_CNT") * dsOutParts.GetColumn(nRow, "UNIT_PRICE");
		dsOutParts.SetColumn(nRow,"AMOUNT",amount);				
	}else if(strColumnID =="UNIT_PRICE"){
		var amount = dsOutParts.GetColumn(nRow, "NOW_UNIT_CNT") * dsOutParts.GetColumn(nRow, "UNIT_PRICE");
		dsOutParts.SetColumn(nRow,"AMOUNT",amount);		
	}
}

