/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    edSearchTxt.Text = Trim(edSearchTxt.Text);
	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e06050010-service:searchSuppCd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSuppCd=dsSuppCd ";

	fce_AddParam("SUPP_NM", edSearchTxt.text);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcGrpCodeExistsCheck(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if ( strColumnID == "GRP_CD" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "그룹코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "그룹코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}

function fcSpecCodeExistsCheck(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if ( strColumnID == "CD" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}


function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSuppCd) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsSuppCd;
	sMandatoryColID   = "SUPP_CD|SUPP_NM|DANGA";
	sMandatoryColName = "소모품코드|소모품명|단가";
	oGrid             = grdSpecCd;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName = "e06050010-service:saveSuppCode";
    sInDataSet   = "dsSuppCd=dsSuppCd:u ";    
    sOutDataSet  = "dsSuppCd=dsSuppCd ";

	fce_AddParam("SUPP_NM", edSearchTxt.text);
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


