/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "specBtnRow" :					
			var nRow = dsSuppCd.addRow();
			if (dsVender.rowcount() == 1) {
				dsSuppCd.SetColumn(nRow, "VENDR_CD", dsVender.GetColumn(0, "VENDR_CD"));
			}
            break;						
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  삭제처리 	
	switch ( obj.GetForm().id ) {
		case "specBtnRow" :
			if ( !fnc_DeleteCheck(dsSuppCd, "chk", "1") ) return;
            break;
    }
}

