/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {	
	if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}    
    var sServiceName = "e06050040-service:searchOrder";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList";       

    //fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("ORDER_STR_DT", calStrDt.Value);
    fce_AddParam("ORDER_END_DT", calEndDt.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


//입고내역 조회
function fcSearchOrderDetail()
{
    var sServiceName = "e06050040-service:searchEntList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEntList=dsEntList";       

    fce_AddParam("JUMUN_NO", dsOutOrderList.GetColumn(dsOutOrderList.row, "JUMUN_NO"));
    fce_AddParam("IO_DT"   , calEntDt.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if(!fnc_DatasetChangeCheck(dsEntList)){
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;		
	}	

	return true;

}

function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

    fce_AddParam("ORDER_STR_DT", calStrDt.Value);
    fce_AddParam("ORDER_END_DT", calEndDt.Value);
	fce_AddParam("IO_DT"   , calEntDt.Value);

    var sServiceName = "e06050040-service:saveEntList";
    var sInDataSet   = "dsEntList=dsEntList:u";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

