//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
}


function dsOutOrderList_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcSearchOrderDetail();
}


function dsEntList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnId == "ENT_CNT") {
		var sOrderCnt = dsEntList.GetColumn(nRow, "ORDER_CNT");
		var sEndCnt = dsEntList.GetColumn(nRow, "END_CNT");
		if ( toNumber(varNewValue) > toNumber(sOrderCnt) - toNumber(sEndCnt)) {
			fnc_Message("현입고수량은 주문수량에서 기입고수량를 뺀 수량을 넘을 수 없습니다.", "N");			
			return;
		}		
	}
}


function fcgrdOnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	if (obj.id == "grdOutEnt") {
		grdOutEnt.SetFocus();
		dsEntList.row = nRow + 1;
		return;
	}
}
