/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }
   return true;

}

//출고내역 조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}    
    var sServiceName = "e06050050-service:searchOutList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutList=dsOutList dsSuppList=dsSuppList";       

    //fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("OUT_STR_DT", calStrDt.Value);
    fce_AddParam("OUT_END_DT", calEndDt.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


//출고 세부내역 조회
function fcSearchOutSuppList()
{
    var sServiceName = "e06050050-service:searchOutSuppList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutSuppList=dsOutSuppList";       

    fce_AddParam("OUT_DT", dsOutList.GetColumn(dsOutList.row, "OUT_DT"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if(!fnc_DatasetChangeCheck(dsOutSuppList)){
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;		
	}	


	// 필수항목체크
	oDataSet          = dsOutSuppList;
	sMandatoryColID   = "SUPP_CD|OUT_QTY";
	sMandatoryColName = "소모품코드|출고수량";
	oGrid             = grdOutSuppList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	var sAcceptNm = "";
	if (!fnc_IsNull(dsOutList.GetColumn(dsOutList.row, "ACCEPT_NM"))) {
		sAcceptNm = dsOutList.GetColumn(dsOutList.row, "ACCEPT_NM");
	} else {
		for (var j=0;j<dsOutSuppList.RowCount();j++) {
			if (!fnc_IsNull(dsOutSuppList.GetColumn(j, "ACCEPT_NM"))) {
				sAcceptNm = dsOutSuppList.GetColumn(j, "ACCEPT_NM");
				break;
			}
		}
	}

	for (var i=0;i<dsOutSuppList.RowCount();i++) {
		if (dsOutSuppList.GetColumn(i, "QTY") < "1") {
			fnc_message(dsOutSuppList.GetColumn(i, "SUPP_NM") + "의 수량을 입력하세요", "N");
			grdOutSuppList.SetFocus();
			dsOutSuppList.row = i;
			return false;
		}
		if (fnc_IsNull(dsOutSuppList.GetColumn(i, "ACCEPT_NM"))) {
			dsOutSuppList.SetColumn(i, "ACCEPT_NM", sAcceptNm);
		}

	}

	return true;

}

function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

    fce_AddParam("OUT_STR_DT", calStrDt.Value);
    fce_AddParam("OUT_END_DT", calEndDt.Value);    
	fce_AddParam("OUT_DT"   ,  dsOutList.GetColumn(dsOutList.row, "OUT_DT"));

    var sServiceName = "e06050050-service:saveOutList";
    var sInDataSet   = "dsOutSuppList=dsOutSuppList:u";
    var sOutDataSet  = "dsOutList=dsOutList";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


