// 출고목록의 라인이 변경된 경우
function dsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsOutList") {
		fcSearchOutSuppList();
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

    if(obj.GetForm().id= "divOutList" ) {
		var nRowType = dsOutList.GetRowType(dsOutList.row);
		if (ToUpper(nRowType) == "INSERT" || ToUpper(nRowType) == "UPDATE") {
			if (fnc_message("저장되지 않는 출고내역의 변경사항을 저장하시겠습니까?","Y")) {
				fcSave(obj);
				return;
			}			
		}		
		var nRow = dsOutList.AddRow();
		dsOutList.SetColumn(nRow, "OUT_DT", today());	
    } 

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
    if(obj.GetForm().id= "divOutList" ) {
		if (dsOutSuppList.rowcount() > 0) {
			if (fnc_message(dsOutList.GetColumn(dsOutList.row, "OUT_DT")+"자 출고내역을 삭제하시겠습니까?", "Y") == true) {
				for (var iCnt=(dsOutSuppList.rowcount() -1);iCnt >= 0;iCnt--) {
					dsOutSuppList.DeleteRow(iCnt);
				}
				fcSave(obj);
			}
		} else {
			dsOutList.DeleteRow(dsOutList.row);
		}
    }
}


// 추가버튼 클릭시
function btnPlus_OnClick(obj)
{
	if(dsOutList.GetRowCount() > 0){	

		var sMbrcd    = dsOutList.getColumn(dsOutList.rowposition, "MBR_CD");
		var sStndYear = dsOutList.getColumn(dsOutList.rowposition, "STND_YEAR");
		var sOutDt    = dsOutList.getColumn(dsOutList.rowposition, "OUT_DT");
		var sAcceptNm = dsOutList.getColumn(dsOutList.rowposition, "ACCEPT_NM");

		var sSuppCd   = dsSuppList.getColumn(dsSuppList.rowposition, "SUPP_CD");
		var sSuppNm   = dsSuppList.getColumn(dsSuppList.rowposition, "SUPP_NM");
		var sSpec     = dsSuppList.getColumn(dsSuppList.rowposition, "SPEC");
		var sCurQty   = dsSuppList.getColumn(dsSuppList.rowposition, "CUR_QTY");
		var sDanga    = dsSuppList.getColumn(dsSuppList.rowposition, "DANGA");

		//품번이 같은게 있는지  체크한다.		
		var iRow = -1;
		iRow = dsOutSuppList.FindRow("SUPP_CD", sSuppCd);
		if(iRow >= 0){
			if (fnc_message("이미 선택한 품목이 존재합니다.\n추가로 등록하시겠습니까?", "Y") == false) {
				grdOutSuppList.SetFocus();
				dsOutSuppList.row = iRow;
				return;
			}
		} 

		var iRow = dsOutSuppList.AddRow();  
		dsOutSuppList.SetColumn(iRow, "OUT_DT",    sOutDt);
		dsOutSuppList.SetColumn(iRow, "SUPP_CD",   sSuppCd);		
		dsOutSuppList.SetColumn(iRow, "SUPP_NM",   sSuppNm);
		dsOutSuppList.SetColumn(iRow, "SPEC",      sSpec);			
		dsOutSuppList.SetColumn(iRow, "DANGA",     sDanga);			
	}	
}

// 마이너스버튼 클릭시
function btnMinus_OnClick(obj)
{
	if (dsOutSuppList.GetRowCount() == 0 ){
		fnc_SetStatus(SNIS_COM_1003,  dsOutSuppList.GetRowCount());		
	}		
	dsOutSuppList.DeleteRow(dsOutSuppList.Row);
}

function fcgrdOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	switch (obj.id) {
		case "grdOutList":
			if (nCell == 1) {
				fnc_SetPopupDivCalendar(obj, nRow, nCell);				
			}
			break;
	}
}


