//현재재고리스트 소모품명으로 조회
function btnSuppSearch_OnClick(obj,nX,nY)
{
    var sServiceName = "e06050050-service:searchSuppList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSuppList=dsSuppList";       

	if(!fcSearchCheck()){
		return;
	}   

    fce_AddParam("SUPPLY_NM", edtSupplyNm.Text);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);		
}

// 엔터키 입력시 현재재고리스트 소모품명으로 조회
function edtSupplyNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13) {	// 엔터키 아스키코드값 : 13
		btnSuppSearch_OnClick();
	}
}
