function fcInit(obj) {	
   if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboTms, "회차")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}
    fcSearchPayDt();

    var sServiceName = "e07020050-service:searchRacePriz";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacePriz=dsOutRacePriz dsOutTmsRacePrizStat=dsOutTmsRacePrizStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("DAY_ORD", cboDayOrd.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
function fcSearchPayDt(obj) {
    var sServiceName = "e07020050-service:searchPayDt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPayDt=dsPayDt";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboTms, "회차")){
		return false;
   }
   return true;
}

function fcSave(obj) {
    var sServiceName = "e07020050-service:saveRacePriz";
    var sInDataSet   = "dsOutRacePriz=dsOutRacePriz:u dsOutTmsRacePrizStat=dsOutTmsRacePrizStat";
    var sOutDataSet  = "dsOutRacePriz=dsOutRacePriz dsOutTmsRacePrizStat=dsOutTmsRacePrizStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("DAY_ORD", cboDayOrd.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

