/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	// 출력용 DataSet 만들기
	buildSumDataSet(); 

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e07029050_race_priz_report.jrf";               //레포트 파일명
	    sRptNm    = "e07029050_race_priz_report_2.jrf";               //레포트 파일명
	var sDataSet  = "race_priz=dsOutRacePriz_rpt";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		sArg += " TMS="+cboTms.Value; 
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	//dsOutRacePriz.DeleteRow(dsOutRacePriz.rowposition);
}

function buildSumDataSet(){

	dsOutRacePriz_rpt.ClearData();
	// 조회 데이터를 출력용 DataSet에 복사
	dsOutRacePriz_rpt.Copy("dsOutRacePriz");

	// 합계 계산
	var tms = Replace(grdRacePriz.GetCellText("Summary",-1,0), ',', '');
	var day_ord = Replace(grdRacePriz.GetCellText("Summary",-1,1), ',', '');
	var race_no = Replace(grdRacePriz.GetCellText("Summary",-1,2), ',', '');
	var race_kinds_cd_nm = Replace(grdRacePriz.GetCellText("Summary",-1,3), ',', '');
	var race_degre_cd_nm = Replace(grdRacePriz.GetCellText("Summary",-1,4), ',', '');
	var race_reg_no = Replace(grdRacePriz.GetCellText("Summary",-1,5), ',', '');
	var racer_no = Replace(grdRacePriz.GetCellText("Summary",-1,6), ',', '');
	var racer_nm = Replace(grdRacePriz.GetCellText("Summary",-1,7), ',', '');
	var rank = Replace(grdRacePriz.GetCellText("Summary",-1,8), ',', '');
	var voil_cd_nm = Replace(grdRacePriz.GetCellText("Summary",-1,9), ',', '');
	var rank_amt = Replace(grdRacePriz.GetCellText("Summary",-1,10), ',', '');
	var day_run_cnt = Replace(grdRacePriz.GetCellText("Summary",-1,11), ',', '');
	var run_amt = Replace(grdRacePriz.GetCellText("Summary",-1,12), ',', '');
	var event_amt = Replace(grdRacePriz.GetCellText("Summary",-1,13), ',', '');
	var safy_amt = Replace(grdRacePriz.GetCellText("Summary",-1,14), ',', '');
	var wait_amt = Replace(grdRacePriz.GetCellText("Summary",-1,15), ',', '');
	var str_amt = Replace(grdRacePriz.GetCellText("Summary",-1,16), ',', '');
	var win_amt = Replace(grdRacePriz.GetCellText("Summary",-1,17), ',', '');
	var etc_amt = Replace(grdRacePriz.GetCellText("Summary",-1,18), ',', '');
	var tot_amt = Replace(grdRacePriz.GetCellText("Summary",-1,19), ',', '');

	// 합계 Row 추가.
	dsOutRacePriz_rpt.AddRow();	
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'TMS', tms);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'DAY_ORD', day_ord);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RACE_NO', race_no);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RACE_KINDS_CD_NM', race_kinds_cd_nm);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RACE_DGRE_CD_NM', race_degre_cd_nm);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RACE_REG_NO', race_reg_no);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RACER_NO', racer_no);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RACER_NM', racer_nm);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RANK', rank);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'VOIL_CD_NM', voil_cd_nm);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RANK_AMT', rank_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'DAY_RUN_CNT', day_run_cnt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'RUN_AMT', run_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'EVENT_AMT', event_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'SAFY_AMT', safy_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'WAIT_AMT', wait_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'STR_AMT', str_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'WIN_AMT', win_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'ETC_AMT', etc_amt);
	dsOutRacePriz_rpt.SetColumn(dsOutRacePriz_rpt.rowposition, 'TOT_AMT', tot_amt);
}
