/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e07020050-service:searchRacePriz" ) {
    	fnc_SetStatus(SNIS_BOA_0003,  dsOutRacePriz.GetRowCount());

	} else if (sServiceName == "e07020050-service:searchLastPrizTms") {
		if (dsLastTms.RowCount() > 0) {
			if (dsLastTms.GetColumn(0, "TMS") <> 0) {
				cboTms.Value = dsLastTms.GetColumn(0, "TMS");
			}
		}
	}



	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

