function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}


//회차 변경시 
function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value, cboTms.Value, "dsOutDayOrd");

}


//회차 DataSet Load 완료 시 
function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fce_GetRaceDayOrd(cboStndYear.Value, cboMbrCd.Value,  cboTms.Value, "dsOutDayOrd");
}

//일차 DataSetLoad 완료 시 
function dsOutDayOrd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fce_AddCodeRow(dsOutDayOrd, "전체", "", "");
	cboDayOrd.Value="";
	fcSearch();
}


//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}


function dsOutTmsRacePrizStat_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var monthPrizFinish = dsOutTmsRacePrizStat.GetColumn(dsOutTmsRacePrizStat.rowposition, 'MONTH_PRIZ_FINISH_YN');
	var tmsPrizFinish = dsOutTmsRacePrizStat.GetColumn(dsOutTmsRacePrizStat.rowposition, 'TMS_PRIZ_FINISH_YN');

	var desc = "";
	if(tmsPrizFinish=='X'){
		desc = '회차 상금 계산 전';
	}else if(tmsPrizFinish == 'N'){
		desc = '회차 상금 계산 처리중';
	}else if(tmsPrizFinish == 'Y'){
		desc = '회차 상금 계산 마감완료';
	}

	if(sInpt=='T'){
		if(tmsPrizFinish=="Y"){
			btnPrizCalc.Visible = false;
			btnConfirm.Visible = false;
			btnConfirmCancel.Visible = true;
			this.divBtnLst.btnSave.Visible = false;
			grdRacePriz.Editable = false;
		}else if(tmsPrizFinish=="N"){
			btnPrizCalc.Visible = true;
			btnConfirm.Visible = true;
			btnConfirmCancel.Visible = false;
			this.divBtnLst.btnSave.Visible = true;
			grdRacePriz.Editable = true;
		}else{
			btnPrizCalc.Visible = true;
			btnConfirm.Visible = true;
			btnConfirmCancel.Visible = false;
			this.divBtnLst.btnSave.Visible = true;
			grdRacePriz.Editable = true;

		}
	}else{
		btnPrizCalc.Visible = false;
		btnConfirm.Visible = false;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = false;
		grdRacePriz.Editable = false;
	}

	labRacePrizStatDesc.Text = desc;


}



function btnPrizCalc_OnClick(obj)
{
	var tmsPrizFinish = dsOutTmsRacePrizStat.GetColumn(dsOutTmsRacePrizStat.rowposition, 'TMS_PRIZ_FINISH_YN');
	if ( tmsPrizFinish =='X' && fnc_Message(SNIS_BOA_G001, "Y" , cboStndYear.Value, cboTms.Value) ) {
		fcCalcRacePriz();	
	}else if ( tmsPrizFinish =='N' && fnc_Message(SNIS_BOA_G002, "Y" , cboStndYear.Value, cboTms.Value) ) {
		fcCalcRacePriz();	
	}
}


function fcCalcRacePriz() {

	if(!fcSearchCheck()){
		return;
	}
	if (calPayDay.Value == "") {
		fnc_message("지급일자를 입력하세요!", "N");
		return;
	}

    var sServiceName = "e07020050-service:calcRacePriz";
    var sInDataSet   = "dsOutTmsRacePrizStat=dsOutTmsRacePrizStat";
    var sOutDataSet  = "dsOutRacePriz=dsOutRacePriz dsOutTmsRacePrizStat=dsOutTmsRacePrizStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
	fce_AddParam("TMS", cboTms.Value);
	fce_AddParam("INST_USER", gdsUser.GetColumn(gdsUser.rowposition, 'USER_ID'));
	fce_AddParam("PAY_DT", calPayDay.Value);	

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



function btnConfirm_OnClick(obj)
{
	dsOutTmsRacePrizStat.SetColumn(dsOutTmsRacePrizStat.rowposition, 'TMS_PRIZ_FINISH_YN', 'Y');
	fcSave();
}

function btnConfirmCancel_OnClick(obj)
{
	dsOutTmsRacePrizStat.SetColumn(dsOutTmsRacePrizStat.rowposition, 'TMS_PRIZ_FINISH_YN', 'N');
	fcSave();
}

function cboDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}
function fcSearchLastTms()
{	
    var sServiceName = "e07020050-service:searchLastPrizTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastTms=dsLastTms";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//상금 변경시 합계 변경 
function dsOutRacePriz_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(varOldValue != varNewValue){
		var tot_amt = ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'RANK_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'RUN_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'EVENT_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'WAIT_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'SAFY_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'WIN_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'STR_AMT'))
					+ ToInteger(dsOutRacePriz.GetColumn(dsOutRacePriz.rowposition, 'ETC_AMT'));
		dsOutRacePriz.SetColumn(dsOutRacePriz.rowposition, 'TOT_AMT', tot_amt);
	}
}

function dsLastTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboTms.Value = dsLastTms.GetColumn(0, "TMS");
}
