//조회 
function fcSearch(obj) {


    var sServiceName = "e07020080-service:searchRacePrizCumulationList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacePrizCumulationList=dsOutRacePrizCumulationList";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("STR_PAY_MONTH", cboStrPayMonth.Value);
    fce_AddParam("END_PAY_MONTH", cboEndPayMonth.Value);
	fce_AddParam("RACER_NM", edRacerNm.Value);


	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


