function fcPrint(obj) {
	buildSumDataSet();
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e07029080_race_priz_cumulation_list.jrf";               //레포트 파일명
	    sRptNm    = "e07029080_race_priz_cumulation_list_2.jrf";               //레포트 파일명(안전수당 삭제)
	var sDataSet  = "dsOutRacePrizCumulationList=dsOutRacePrizCumulationList";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		sArg += " STR_PAY_MONTH="+cboStrPayMonth.Value; 
		sArg += " END_PAY_MONTH="+cboEndPayMonth.Value; 
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	dsOutRacePrizCumulationList.DeleteRow(dsOutRacePrizCumulationList.rowposition);
}



function buildSumDataSet(){
	var raceNo = grdRacePrizCumulationList.GetCellText("Summary",-1,0);	 	//선수 번호 필드 
	var racerNm = grdRacePrizCumulationList.GetCellText("Summary",-1,1);	 	//선수명 필드 
	var runCnt = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,2), ',', '');	 	//출주수  필드 
	var suppAmt01= Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,3), ',', '');	 	//성적상금필드 
	var suppAmt02 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,4), ',', '');  	//출전수당 필드 
	var suppAmt31 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,5), ',', '');  	//이벤트수당 필드
	var suppAmt03 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,6), ',', '');		//안전수당 필드 
	var suppAmt04 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,7), ',', '');		//대기수당 필드 
	var suppAmt05 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,8), ',', '');		//출발무사고 필드 
	var suppEtcAmt = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,9), ',', '');		//기타수당 필드 
	var suppTotAmt = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,10), ',', '');		//합계금액 필드 
	var aidAmt10 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,11), ',', '');		//식대 필드 
	var aidAmt04 = Replace(grdRacePrizCumulationList.GetCellText("Summary",-1,12), ',', '');		//선수회비 필드 


	dsOutRacePrizCumulationList.AddRow();
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'RACER_NO', raceNo);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'RACER_NM', racerNm);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'RUN_CNT', runCnt);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_AMT_01', suppAmt01);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_AMT_02', suppAmt02);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_AMT_31', suppAmt31);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_AMT_03', suppAmt03);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_AMT_04', suppAmt04);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_AMT_05', suppAmt05);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_ETC_AMT', suppEtcAmt);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'SUPP_TOT_AMT', suppTotAmt);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'AID_AMT_10', aidAmt10);
	dsOutRacePrizCumulationList.SetColumn(dsOutRacePrizCumulationList.rowposition, 'AID_AMT_04', aidAmt04);



}
