function dsOutMonthPayStat_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var monthPrior = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIOR');
	var monthPrizFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIZ_FINISH_YN');
	var monthActFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ACT_FINISH_YN');

	var desc = monthPrior;
	if(monthPrizFinish=='X'){
		desc += '   ,월 상금 계산 전';
	}else if(monthPrizFinish == 'N'){
		desc += '  ,월 상금 계산 처리중';
	}else if(monthPrizFinish == 'Y'){
		desc += '  ,월 상금 계산 마감완료';
	}

	if(monthActFinish=='X'){
		desc += '   ,회계 계산 전';
	}else if(monthActFinish == 'N'){
		desc += '  ,회계처리중';
	}else if(monthActFinish == 'Y'){
		desc += '  ,회계(상금추가) 마감완료';
	}

	if(monthActFinish=="Y"){
		btnConfirm.Visible = false;
		btnConfirmCancel.Visible = true;
		this.divBtnLst.btnSave.Visible = false;
		grdMonthPay.Editable = false;
		tbnExcelUpload.Visible = false;
	}else if(monthActFinish=="N"){
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthPay.Editable = true;
		tbnExcelUpload.Visible = true;
	}else{
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthPay.Editable = true;
		tbnExcelUpload.Visible = true;

	}
	labMonthPayStatDesc.Text = desc;
}




function btnConfirm_OnClick(obj)
{
	var monthPrizFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIZ_FINISH_YN');
	if(monthPrizFinish !="Y"){
		alert("선수 상금계산이마감되지 않았습니다.\n회계산을 마무리 할 수 없습니다.");
		return false;
	}else{
		dsOutMonthPayStat.SetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ACT_FINISH_YN', 'Y');
		fcSave();
	}
}

function btnConfirmCancel_OnClick(obj)
{
	dsOutMonthPayStat.SetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ACT_FINISH_YN', 'N');
	fcSave();
}


function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value < "2014") {
		stPayMonth.Value = "월";
	} else {
		stPayMonth.Value = "회차"; 
		fce_GetRaceTms(cboStndYear.Value, CURR_USER_MBR_CD, null, "dsOutPayMonth");	
	}
}

function cboPayMonth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value == "2014" && toNumber(cboPayMonth.Value) <= 7) {
		stPayMonth.text = "월";
	} else if (cboStndYear.Value == "2014") {
		stPayMonth.text = "회차";
	}
	fcSearch();
}

function dsOutMonthPay_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'AID_AMT_11'){ //상해보험표 
		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'SUPP_AMT_14', varNewValue);
	}else if(strColumnID == 'AID_AMT_13'){ //현무제공
		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'SUPP_AMT_15', varNewValue);
	}else if(strColumnID == 'AID_AMT_14'){//기타항목
		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'SUPP_AMT_16', varNewValue);
	}else if(strColumnID == 'SUPP_AMT_12'){//선수연금(사업자 부담)
		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'AID_AMT_12', varNewValue);
	}
}




//Excel Upload
function tbnExcelUpload_OnClick(obj)
{
	var strFileName;
	var nRow;
	var TR_AMT_IN;
	var TR_AMT_OUT;

	FileDialog.Type = "OPEN";
	FileDialog.Filter = "Excel(.xls)|*.xls|Excel(.xlsx)|*.xlsx|";

	if (!FileDialog.open()){
		return;
	}
	dsOutSheet.ClearData();
	dsOutMonthPayEtc.ClearData();
	strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;

	ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
	nRow = dsOutSheet.RowCount();

	grdMonthPay.Redraw = false;

	for (var i = 3; i < nrow; i++)
	{
		if( trim(dsOutSheet.GetColumn(i, 'Col04'))=='계:')continue;
		dsOutMonthPayEtc.AddRow();
		dsOutSheet.CopyToRow(i, dsOutMonthPayEtc, dsOutMonthPayEtc.rowposition, "STND_YEAR=Col01,PAY_MONTH=Col02,PAY_TPE=Col03,NUM=Col04,RACER_NO=Col05,RACER_NM=Col06,SUPP_AMT_08=Col07,SUPP_AMT_09=Col08,SUPP_AMT_12=Col09,AID_AMT_11=Col10,AID_AMT_13=Col11,AID_AMT_14=Col12,AID_AMT_05=Col13,AID_AMT_07=Col14,AID_AMT_06=Col15,AID_AMT_08=Col16");

		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'STND_YEAR', cboStndYear.Value);
		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'PAY_MONTH', cboPayMonth.Value);
		dsOutMonthPayEtc.SetColumn(dsOutMonthPayEtc.rowposition, 'PAY_TPE', '1');
	}
	grdMonthPay.Redraw = true;
}



function fcSearchLastTms()
{	
    var sServiceName = "e07020065-service:searchLastPayTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastTms=dsLastTms";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD",    CURR_USER_MBR_CD);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
function dsLastTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (dsLastTms.rowcount() > 0) {
		cboPayMonth.Value = dsLastTms.GetColumn(0, "TMS");
	}
}
