function fcToExcel(obj) {

	if(tbList.TabIndex ==0){
		fnc_OpenExcelExport("tbList.tbMonthPayList.grdMonthPayList");
	}else if(tbList.TabIndex ==1){
		fnc_OpenExcelExport("tbList.tbMonthPayFncList.grdMonthPayList");
	}else{
		fnc_OpenExcelExport("tbList.tbMonthPayTotList.grdMonthPayList");
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	if(tbList.TabIndex ==0){
		var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
		var sRptNm    = "e07039020_month_pay_list_report.jrf";               //레포트 파일명
		var sDataSet  = "dsOutMonthPayList=dsOutMonthPayList";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_YEAR="+cboStndYear.Value;
			sArg += " PAY_MONTH="+cboPayMonth.Value; 
			sArg += " IMG_URL="+logImgUrl; 
		var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if(tbList.TabIndex ==1){
		var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
		var sRptNm    = "e07039020_month_pay_fnc_list_report.jrf";               //레포트 파일명
		var sDataSet  = "dsOutMonthPayFncList=dsOutMonthPayFncList";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_YEAR="+cboStndYear.Value;
			sArg += " PAY_MONTH="+cboPayMonth.Value; 
			sArg += " IMG_URL="+logImgUrl; 
		var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else{
		var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
		var sRptNm    = "e07039020_month_pay_tot_list_report.jrf";               //레포트 파일명
		var sDataSet  = "dsOutMonthPayTotList=dsOutMonthPayTotList";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_YEAR="+cboStndYear.Value;
			sArg += " PAY_MONTH="+cboPayMonth.Value; 
			sArg += " IMG_URL="+logImgUrl; 
		var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}
}
