function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value < "2014") {
		stPayMonth.Value = "월";
	} else {
		stPayMonth.Value = "회차"; 
		fce_GetRaceTms(cboStndYear.Value, CURR_USER_MBR_CD, null, "dsOutPayMonth");	
	}
}

function cboPayMonth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value == "2014" && toNumber(cboPayMonth.Value) <= 7) {
		stPayMonth.text = "월";
	} else if (cboStndYear.Value == "2014") {
		stPayMonth.text = "회차";
	}
	fcSearch();
}



function dsOutMonthPayStat_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var monthEtcFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ETC_FINISH_YN');

	var desc = "";

	if(monthEtcFinish == 'N'){
		desc += '  기타지급 처리중 ';
	}else if(monthEtcFinish == 'Y'){
		desc += '  기타지급 마감완료';
	}

	if(monthEtcFinish=="Y"){
		btnConfirm.Visible = false;
		btnConfirmCancel.Visible = true;
		this.divBtnLst.btnSave.Visible = false;
		grdMonthFncEtcPay.Editable = false;
	}else if(monthEtcFinish=="N"){
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthFncEtcPay.Editable = true;
	}else{
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthFncEtcPay.Editable = true;

	}
	labMonthPayStatDesc.Text = desc;
}

function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	//높이 조절
	var nHeight = nY;
	var nHeightGrd = nHeight - 48;
	if (nY <= 590) {
		nHeight = 518;		
	} else {	
		nHeight = nY - (tbList.Top +2);
	}
	nHeightGrd = nHeight - 31;
	tbList.Height = nHeight;
	tbList.tbMonthPayList.grdMonthPayList.Height = nHeightGrd;
	tbList.tbMonthPayTotList.grdMonthPayList.Height = nHeightGrd;
	tbList.tbMontPayFncList.grdMonthPayList.Height = nHeightGrd;
}


function fcSearchLastTms()
{	
    var sServiceName = "e07020065-service:searchLastPayTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastTms=dsLastTms";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD",    CURR_USER_MBR_CD);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
function dsLastTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (dsLastTms.rowcount() > 0) {
		cboPayMonth.Value = dsLastTms.GetColumn(0, "TMS");
	}
}
