/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	buildSumDataSet();
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
		var sRptNm    = "e07039021_bk_trans_list_report.jrf";               //레포트 파일명
		var sDataSet  = "dsOutBkTransList=dsOutBkTransList";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_YEAR="+cboStndYear.Value;
			sArg += " PAY_MONTH="+cboPayMonth.Value; 
			sArg += " IMG_URL="+logImgUrl; 
		var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
		dsOutBkTransList.DeleteRow(dsOutBkTransList.rowposition);
}


function buildSumDataSet(){
	var num = grdBkTransList.GetCellText("Summary",-1,0);	 	//순번
	var racerNo = grdBkTransList.GetCellText("Summary",-1,1);	 	//선수번호  
	var racerNm = Replace(grdBkTransList.GetCellText("Summary",-1,2), ',', '');	 	//선수명
	var resNo = Replace(grdBkTransList.GetCellText("Summary",-1,3), ',', '');  	//주민등록번호 
	var bkNm = Replace(grdBkTransList.GetCellText("Summary",-1,4), ',', '');		//은행명
	var bkAccunt = Replace(grdBkTransList.GetCellText("Summary",-1,5), ',', '');		//계좌번호  
	var realSuppAmt = Replace(grdBkTransList.GetCellText("Summary",-1,6), ',', '');		//실지급액

	dsOutBkTransList.AddRow();
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'NUM', num);
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'RACER_NO', racerNo);
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'RACER_NM', racerNm);
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'RES_NO', resNo);
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'BK_NM', bkNm);
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'BK_ACCUNT', bkAccunt);
	dsOutBkTransList.SetColumn(dsOutBkTransList.rowposition, 'REAL_SUPP_AMT', realSuppAmt);

}

