/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {	
	if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
	dsOutMonthPay.ClearData();    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
	if(!fnc_IsMandatory(cboStndYear, "지급년도")){
		return false;
	}
	return true;	
}

function fcSearch(obj) {	
	if(!fcSearchCheck()){
		return;
	}
	var sServiceName = "e07030040-service:searchMonthPay";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutMonthPay=dsOutMonthPay";

    fce_AddParam("STND_YEAR", cboStndYear.Value);	
    fce_AddParam("NM_KOR", edNmKor.Text);    
    fce_AddParam("SUPP_AMT_10", edSuppAmt10.Value);    
    fce_AddParam("AWARD_AMT", edAwardAmt.Value);    

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

