/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var msg = "";
	if(edSuppAmt10.Value=='1' && edAwardAmt.Value == '1'){
		msg = "(식대/포상금포함)";
	}else if(edSuppAmt10.Value == '1'){
		msg = "(식대포함)";
	}else if(edAwardAmt.Value == '1'){
		msg = "(포상금포함)";
	}

	buildSumDataSet();
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
		var sRptNm    = "e07039040_priz_list_report.jrf";               //레포트 파일명
		var sDataSet  = "dsOutMonthPay=dsOutMonthPay";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_YEAR="+cboStndYear.Value;
			sArg += " MSG="+msg; 
			sArg += " IMG_URL="+logImgUrl; 
		var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
		dsOutMonthPay.DeleteRow(dsOutMonthPay.rowposition);
}


function buildSumDataSet(){
	var num = grdMonthPay.GetCellText("Summary",-1,0);	 	//순번
	var racerNo = grdMonthPay.GetCellText("Summary",-1,1);	 	//선수번호  
	var racerNm = grdMonthPay.GetCellText("Summary",-1,2);	//선수명
	var month01 = Replace(grdMonthPay.GetCellText("Summary",-1,3), ',', '');  	//1월
	var month02 = Replace(grdMonthPay.GetCellText("Summary",-1,4), ',', '');  	//2월
	var month03 = Replace(grdMonthPay.GetCellText("Summary",-1,5), ',', '');  	//3월
	var month04 = Replace(grdMonthPay.GetCellText("Summary",-1,6), ',', '');  	//4월
	var month05 = Replace(grdMonthPay.GetCellText("Summary",-1,7), ',', '');  	//5월
	var month06 = Replace(grdMonthPay.GetCellText("Summary",-1,8), ',', '');  	//6월
	var month07 = Replace(grdMonthPay.GetCellText("Summary",-1,9), ',', '');  	//7월
	var month08 = Replace(grdMonthPay.GetCellText("Summary",-1,10), ',', '');  	//8월
	var month09 = Replace(grdMonthPay.GetCellText("Summary",-1,11), ',', '');  	//9월
	var month10 = Replace(grdMonthPay.GetCellText("Summary",-1,12), ',', '');  	//10월
	var month11 = Replace(grdMonthPay.GetCellText("Summary",-1,13), ',', '');  	//11월
	var month12 = Replace(grdMonthPay.GetCellText("Summary",-1,14), ',', '');  	//12월
	var monthSum = Replace(grdMonthPay.GetCellText("Summary",-1,15), ',', '');  //합계 


	dsOutMonthPay.AddRow();
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'NUM', num);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'RACER_NO', racerNo);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'NM_KOR', racerNm);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH01', month01);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH02', month02);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH03', month03);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH04', month04);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH05', month05);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH06', month06);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH07', month07);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH08', month08);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH09', month09);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH10', month10);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH11', month11);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH12', month12);
	dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'MONTH_SUM', monthSum);


}
