function edNmKor_OnClick(obj)
{
	var sArg = "Arg_sRacerNm=" ;
	var sResponse = Dialog("form::E07040021.xml", sArg, 834, 450, "");

	if ( !fnc_IsNull(sResponse) ) {
		edRacerNo.Text = sResponse[0];
		edRacerNm.Text =  sResponse[1];
	}
}

function edRacerNo_OnClick(obj)
{
	var sArg = "Arg_sRacerNm=" ;
	var sResponse = Dialog("form::E07040021.xml", sArg, 834, 450, "");

	if ( !fnc_IsNull(sResponse) ) {
		edRacerNo.Text = sResponse[0];
		edRacerNm.Text =  sResponse[1];
	}
}


function grdIncomIssu_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if(nCell == 14) {
		// 인쇄
		if(dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'PRS_STAT_CD')=='002'){
			if ( dsOutIncomIssu.getColumn(dsOutIncomIssu.rowposition, 'ISSU_YN') =='발급'){
				fnc_Message(SNIS_BOA_G014);
			}else if(fnc_Message(SNIS_BOA_G013, "Y" ) ){
				fcPrintTaxReport();
			}else{
				fnc_Message(SNIS_BOA_G014);
			}
		}
		// 삭제
		else {
			if(confirm('선택된 자료를 삭제 하시겠습니까?')){
				var STND_YEAR_MONTH = dsOutIncomIssu.GetColumn(nRow,"STND_YEAR_MONTH");
				var INCOM_ISSU_SEQ = dsOutIncomIssu.GetColumn(nRow,"INCOM_ISSU_SEQ");

				var ServiceName = "e07030050-service:deleteIncomIssu";
				var sInDataSet   = "dsOutIncomIssu=dsOutIncomIssu";
				var sOutDataSet  = "dsOutIncomIssu=dsOutIncomIssu";
				fce_AddParam("STND_YEAR_MONTH", STND_YEAR_MONTH);		
				fce_AddParam("INCOM_ISSU_SEQ", INCOM_ISSU_SEQ);

				fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack");
			}
		}
	}
}


function fcAccCheck() {
   if(!fnc_IsMandatory(edRacerNo, "선수등록번호 ")){
		return false;
   }else if(!fnc_IsMandatory(edIssuCnt, "발급건수")){
		return false;
   }else if(!fnc_IsMandatory(edUseGoal, "사용목적 ")){
		return false;
   }else if(!fnc_IsMandatory(edSubmitOffice, "제출처 ")){
		return false;
   }

   return true;
}

function fcMultiCheck() {
   if(!fnc_IsMandatory(edIssuCnt, "발급건수")){
		return false;
   }else if(!fnc_IsMandatory(edUseGoal, "사용목적 ")){
		return false;
   }else if(!fnc_IsMandatory(edSubmitOffice, "제출처 ")){
		return false;
   }

   return true;  
}


